INSERT INTO `ques` (`id`, `ques`, `ans1`, `ans2`, `ans3`, `ans4`, `correct_ans`, `important`, `ques_image`, `detail`, `type`, `pack`, `exam`) VALUES
(3, 'Phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại là gì?', 'Phần mặt đường và lề đường', 'Phần đường xe chạy', 'Phần đường xe cơ giới', '', 2, 0, '', '', 'luat', 'A1', 1),
(4, '\"Làn đường\" là gì?', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, sử dụng cho xe chạy', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn', 'Là đường cho xe ô tô chạy, dừng, đỗ an toàn', '', 2, 0, '', '', 'luat', 'A1', 2),
(5, 'Trong các khái niệm dưỡi đây, \"dải phân cách\" được hiểu như thế nào là đúng?', 'Là bộ phận của đường để ngăn cách không cho các loại xe vào những nơi không được phép', 'Là bộ phận của đường để phân tách phần đường xe chạy và hành lang an toàn giao thông', 'Là bộ phận của đường để phân chia mặt đường thành hai chiều xe chạy riêng biệt hoặc để phân chia phần đường của xe cơ giới và xe thô sơ', '', 3, 0, '', '', 'luat', 'A1', 3),
(6, '\"Dải phân cách\" trên đường bộ gồm những loại nào?', 'Dải phân cách gồm loại cố định và loại di động', 'Dải phân cách gồm tường chống ồn, hộ lan cứng và hộ lan mềm', 'Dải phân cách gồm giá long môn và biển báo hiệu đường bộ', '', 1, 0, '', '', 'luat', 'A1', 4),
(7, 'Người lái xe được hiểu như thế nào trong các khái niệm dưới đây?', 'Là người điều khiển xe cơ giới', 'Là người điều khiển xe thô sơ', 'Là người điều khiển xe có súc vật kéo', '', 1, 0, '', '', 'luat', 'A1', 5),
(8, 'Đường mà trên đó phương tiện tham gia giao thông được các phương tiện giao thông đến từ hướng khác nhường đường khi qua nơi đường giao nhau, được cắm biển báo hiệu đường ưu tiên là loại đường gì? ', 'Đường không ưu tiên', 'Đường tỉnh lộ', 'Đường quốc lộ', 'Đường ưu tiên', 4, 0, '', '', 'luat', 'A1', 6),
(9, 'Khái niệm \"phương tiện giao thông cơ giới đường bộ\" được hiểu thế nào là đúng?', 'Gồm xe ô tô; máy kéo; xe moto 2 bánh; xe moto 3 bánh; xe gắn máy; xe cơ giới dùng cho người khuyết tật và xe máy chuyên dùng.', 'Gồm xe ô tô; máy kéo; rơ mooc hoặc sơ mi rơ mooc được kéo bởi ô tô; máy kéo; xe moto 2 bánh; xe moto 3 bánh; xe gắn máy (kể cả xe máy điện) và các loại xe tương tự', '', '', 2, 0, '', '', 'luat', 'A1', 7),
(10, 'Khái niệm \"phương tiện giao thông thô sơ đường bộ\" được hiểu như thế nào là đúng?', 'Gồm xe đạp (kể cả xe đạp máy, xe đạp điện), xe xích lô, xe lăn dùng cho người khuyết tật, xe súc vật kéo và các loại xe tương tự', 'Gồm xe đạp (kể cả xe đạp máy, xe đạp điện), xe gắn máy, xe cơ giới dùng cho người khuyết tật và xe máy chuyên dụng', 'Gồm xe ô tô, máy kéo, rơ mooc hoặc sơ mi rơ mooc được kéo bởi ô tô, máy kéo', '', 1, 0, '', '', 'luat', 'A1', 8),
(11, '\"Phương tiện tham gia giao thông đường bộ\" gồm những loại nào?', 'Phương tiện giao thông cơ giới đường bộ', 'Phương tiện giao thông thô sơ đường bộ và xe máy chuyên dụng', 'Cả 1 và 2', '', 3, 0, '', '', 'luat', 'A1', 1),
(12, '\"Người tham gia giao thông đường bộ\" gồm những đối tượng nào?', 'Người điều khiển, người sử dụng phương tiện tham gia giao thông đường bộ', 'Người điều khiển, dẫn dắt súc vật, người đi bộ trên đường bộ', 'Cả 1 và 2', '', 3, 0, '', '', 'luat', 'A1', 2),
(13, 'Biển nào dưới đây xe gắn máy được phép đi vào?', 'Biển 1', 'Biển 2', 'Cả 2 biển', '', 3, 0, '309.jpg', 'Biển 1 cấm xe mô tô thì xe gắn máy vẫn được đi vào.\r\nBiển 2 cấm xe ô tô, không cấm xe gắn máy.', 'bienbao', 'A1', 5),
(14, 'Biển báo nào báo hiệu cấm xe moto 2 bánh đi vào', 'Biển 1', 'Biển 2', 'Biển 3', '', 1, 0, '310.jpg', 'Biển 2 và 3 cấm ô tô, xe mô tô vào được.', 'bienbao', 'A1', 6),
(15, 'Khi gặp biển nào thì xe mô tô hai bánh được đi vào?', 'Không biển nào', 'Biển 1 và 2', 'Biển 2 và 3', 'Cả 3 biển', 3, 0, '311.jpg', 'Biển 2 và 3 cấm ô tô, xe mô tô vào được.', 'bienbao', 'A1', 7),
(16, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe tải, xe khách, xe con, mô tô.', 'Xe tải, mô tô, xe khách, xe con.', 'Xe khách, xe tải, xe con, mô tô.', 'Mô tô, xe khách, xe tải, xe con.', 2, 0, '487.jpg', 'Theo thứ tự:\r\n1. Xe tải đi trên đường ưu tiên, đi thẳng.\r\n2. Xe mô tô đi trên đường ưu tiên, rẽ trái.\r\n3. Xe khách đi trên đường không ưu tiên, đi thẳng.\r\n4. Xe con đi trên đường không ưu tiên, rẽ rái.', 'sahinh', 'A1', 6),
(17, 'Người điều khiển phương tiện tham gia giao thông đường bộ” gồm những đối tượng nào dưới đây?', 'Người điều khiển xe cơ giới, người điều khiển xe thô sơ.', 'Người điều khiển xe máy chuyên dùng tham gia giao thông đường bộ.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'A1', 3),
(18, 'Khái niệm “người điều khiển giao thông” được hiểu như thế nào là đúng ?', 'Là người điều khiển phương tiện tham gia giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', 'Là cảnh sát giao thông, người được giao nhiệm vụ hướng dẫn giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt', 'Là người tham gia giao thông tại nơi thi công, nơi ùn tắc giao thông, ở bến phà, tại cầu đường bộ đi chung với đường sắt.', '', 2, 0, '', '', 'luat', 'A1', 4),
(19, 'Trong các khái niệm dưới đây khái niệm “dừng xe” được hiểu như thế nào là đúng ?', 'Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian để cho người lên, xuống phương tiện, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', 'Là trạng thái đứng yên tạm thời của phương tiện giao thông trong một khoảng thời gian cần thiết đủ để cho người lên, xuống phương tiện, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', 'Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian giữa 02 lần vận chuyển hàng hoá hoặc hành khách.', '', 2, 0, '', '', 'luat', 'A1', 5),
(20, 'Khái niệm “đỗ xe” được hiểu như thế nào là đúng ?', 'Là trạng thái đứng yên của phương tiện giao thông có giới hạn trong một khoảng thời gian cần thiết đủ để cho người lên, xuống phương tiện đó, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', 'Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian.', '', '', 2, 0, '', '', 'luat', 'A1', 6),
(21, 'Cuộc đua xe chỉ được thực hiện khi nào ?', 'Diễn ra trên đường phố không có người qua lại', 'Được người dân ủng hộ', 'Được cơ quan có thẩm quyền cấp phép.', '', 3, 1, '', '', 'luat', 'A1', 7),
