(178, 'Theo tín hiệu đèn, xe nào được phép đi?', 'Xe con và xe khách.', 'Mô tô.', '', '', 1, 0, '499.jpg', 'Xe con và xe khách đang ở làn đường có tín hiệu đèn xanh nên được phép đi.\r\n', 'sahinh', 'A1', 2),
(179, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe khách, xe tải, mô tô. ', 'Xe tải, xe con, mô tô.', 'Xe khách, xe con, mô tô.', '', 1, 0, '503.jpg', '1. Xe khách: Sai làn, đi thẳng đèn đang đỏ;\r\n2. Xe tải: Sai làn: Đi trên làn đi thẳng trong khi thực hiện rẽ trái.\r\n3. Xe mô tô: Sai làn: Đi trên làn rẽ trái trong khi thực hiện rẽ phải.\r\nNên đáp án đúng là xe khách, xe tải và xe mô tô đều vi phạm quy tắc giao thông.\r\n', 'sahinh', 'A1', 3),
(180, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe khách, xe tải, mô tô, xe con. ', 'Xe con, xe khách, xe tải, mô tô.', 'Mô tô, xe tải, xe khách, xe con. ', 'Mô tô, xe tải, xe con, xe khách.', 3, 0, '504.jpg', 'Giao nhau cùng cấp có vòng xuyến: Chưa vào vòng xuyến thì ưu tiên xe bên phải; đã vào vòng xuyến ưu tiên xe từ bên trái tới.\r\nToàn bộ các xe chưa vào vòng xuyến nên ưu tiên xe bên phải.\r\nThứ tự sẽ là mô tô, xe tải, xe khách, xe con.\r\n', 'sahinh', 'A1', 4),
(181, 'Trong trường hợp này xe nào đỗ vi phạm quy tắc giao thông?', 'Xe tải. ', 'Xe con và mô tô,', 'Cả ba xe.', 'Xe con và xe tải.', 1, 0, '505.jpg', 'Biển cấm đỗ xe tải (ở biển phụ) nên chỉ có xe tải vi phạm.\r\n', 'sahinh', 'A1', 5),
(182, 'Theo hướng mũi tên, những hướng nào xe gắn máy đi được?', 'Cả ba hướng.', 'Chỉ hướng 1 và 3. ', 'Chỉ hướng 1.', '', 1, 0, '507.jpg', 'Bạn lưu ý câu hỏi này về hướng đi của xe GẮN MÁY. Hướng 1 và 3 chắn chắn là đi được rồi. \r\nVới hướng 2, biển báo cấm mô tô đi vào chỉ có tác dụng với xe mô tô nên xe gắn máy không có hiệu lực. DO đó vẫn đi vào bình thường. \r\n', 'sahinh', 'A1', 6),
(183, 'Xe nào đỗ vi phạm quy tắc giao thông?', 'Cả hai xe. ', 'Không xe nào vi phạm.', 'Chỉ xe mô tô vi phạm.', 'Chỉ xe tải vi phạm.', 1, 0, '508.jpg', 'Biển cấm đỗ xe ở cả phía trước và phía sau biển (ở biển báo phụ) nên cả 2 xe vi phạm.\r\n', 'sahinh', 'A1', 7),
(184, 'Xe nào đỗ vi phạm quy tắc giao thông?', 'Chỉ mô tô.', 'Chỉ xe tải.', 'Cả ba xe.', 'Chỉ mô tô và xe tải.', 3, 0, '509.jpg', 'Xe tải đậu trái đường; Xe con và mô tô đậu xe đè vạch nên cả 3 xe vi phạm.\r\n', 'sahinh', 'A1', 8),
(185, 'Xe tải kéo mô tô ba bánh như hình này có đúng quy tắc giao thông không?', 'Đúng.', 'Không đúng.', '', '', 2, 0, '510.jpg', 'Xe tải kéo xe mô tô 3 bánh như trên hình là không đúng vì đi vào đường có biển P.108 “Cấm ô tô kéo moóc” kể cả xe máy, xe ô tô khách kéo theo rơ moóc đi qua.\r\n', 'sahinh', 'A1', 1),
(186, 'Xe nào được quyền đi trước trong trường hợp này?', 'Xe con.', 'Xe mô tô.', '', '', 2, 0, '517.jpg', 'Cả 2 xe đều gặp đèn xanh nên áp dụng quy tắc đường cùng cấp: Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái.\r\nNên đáp án đúng là xe mô tô rẽ phải được quyền đi trước. Xe con rẽ trái phải nhường đường.\r\n', 'sahinh', 'A1', 2),
(187, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe con (A), mô tô, xe con (B), xe đạp. ', 'Xe con (B), xe đạp, mô tô, xe con (A). ', 'Xe con (A), xe con (B), mô tô + xe đạp. ', 'Mô tô + xe đạp, xe con (A), xe con (B).', 4, 0, '520.jpg', 'Thứ tự ưu tiên đường cùng cấp: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái.\r\n', 'sahinh', 'A1', 3),
(188, 'Xe nào được quyền đi trước trong trường hợp này?', 'Mô tô. ', 'Xe con.', '', '', 1, 0, '525.jpg', 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ trái – Đi thẳng – Rẽ trái. Xe mô tô rẽ trái từ đường ưu tiên vào đường ưu tiên (theo biển báo và biển phụ) nên được quyền đi trước.\r\n', 'sahinh', 'A1', 4),
(189, 'Xe nào vi phạm quy tắc giao thông?', 'Xe khách. ', 'Mô tô. ', 'Xe con.', 'Xe con và mô tô.', 3, 0, '527.jpg', 'Xe con quay đầu đè vạch liền trên đường nên vi phạm.\r\n', 'sahinh', 'A1', 5),
(190, 'Các xe đi như thế nào là đúng quy tắc giao thông?', 'Các xe ở phía tay phải và tay trái của người điều khiển được phép đi thắng. ', 'Cho phép các xe ở mọi hướng được rẽ phải. ', 'Tất cả các xe phải dừng lại trước ngã tư, trừ những xe đã ở trong ngã tư được phép tiếp tục đi.', '', 3, 0, '528.jpg', 'Giơ tay thẳng đứng: Tất cả dừng, trừ xe đã ở trong ngã tư được phép đi;\r\nGiang tay ngang: Trái phải đi; Trước sau dừng; \r\nTay phải giơ trước: sau, phải dừng, trước rẽ trái, trái đi các hướng, người đi bộ qua đường đi sau người điều khiển.\r\n', 'sahinh', 'A1', 6),
(191, 'Theo hướng mũi tên, xe nào được phép đi?', 'Mô tô, xe con. ', 'Xe con, xe tải. ', 'Mô tô, xe tải.', 'Cả ba xe.', 3, 0, '529.jpg', 'Giang ngang tay: Trái phải đi; Trước sau dừng. Xe mô tô và xe tải được phép đi là đáp án đúng.\r\n', 'sahinh', 'A1', 7),
(192, 'Trong hình dưới đây, xe nào chấp hành đúng quy tắc giao thông?', 'Chỉ xe khách, mô tô, ', 'Tất cả các loại xe trên. ', 'Không xe nào chấp hành đúng quy tắc giao thông.', '', 2, 0, '538.jpg', 'Xe khách chờ đèn đỏ rẽ trái. Xe tải đi thẳng và rẽ phải theo tín hiệu đèn xanh. Xe con đi thẳng và rẽ phải theo tín hiệu đèn xanh. Xe mô tô chờ đèn đỏ rẽ trái. Nên tất cả xe đều chấp hành.\r\n', 'sahinh', 'A1', 8),
(193, 'Theo hướng mũi tên, những hướng nào xe mô tô được phép đi?', 'Cả ba hướng. ', 'Hướng 1 và 2.', 'Hướng 1 và 3.', 'Hướng 2 và 3.', 3, 0, '539.jpg', 'Hướng 2 có biển số P.104 “Cấm mô tô”\r\nHướng 3 Biển số P.103a “Cấm ô tô” nhưng không cấm mô tô. Nên đáp án đúng là hướng 1 và hướng 3.\r\n', 'sahinh', 'A1', 1),
(194, 'Trong trường hợp này, thứ tự xe đi như thế nào là đúng quy tắc giao thông?', 'Xe công an, xe quân sự, xe con + mô tô. ', 'Xe quân sự, xe công an, xe con + mô tô. ', 'Xe mô tô + xe con, xe quân sự, xe công an.', '', 2, 0, '540.jpg', 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái.\r\n1. Xe quân sự: Xe ưu tiên cùng cấp xe công an nhưng đi thẳng;\r\n2. Xe công an: Xe ưu tiên; rẽ trái;\r\n3. Xe con và xe mô tô: Cùng đi thẳng.\r\n', 'sahinh', 'A1', 2),
(195, 'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?', 'Xe con (E), mô tô (c). ', 'Xe tải (A), mô tô (D).', 'Xe khách (B), mô tô (C).', 'Xe khách (B), mô tô (D).', 1, 0, '543.jpg', 'Xe mô tô C chạy vào làn danh riêng xe ô tô: Vi phạm; Xe con E chạy vào làn danh riêng xe máy: Vi phạm.\r\n', 'sahinh', 'A1', 3),
(196, 'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?', 'Xe con (B), mô tô (C). ', 'Xe con (A), mô tô (C). ', 'Xe con (E), mô tô (D).', 'Tất cả các loại xe trên.', 3, 0, '548.jpg', 'Nhìn vào bảng chỉ dẫn loại phương tiện theo làn đường: Xe con (E) đi trên làn danh riêng cho mô tô nên vi phạm: Xe mô tô (D) chạy trên làn dành riêng cho ô tô nên cũng vi phạm.\r\n', 'sahinh', 'A1', 4),
(197, 'Bạn có được phép vượt xe mô tô phía trước không?', 'Cho phép.', 'Không được vượt.', '', '', 2, 0, '556.jpg', 'Tại nơi giao nhau, trên đoạn đường có làn đường danh cho người đi bộ cắt ngang qua thì không được phép vượt.\r\n', 'sahinh', 'A1', 5),
(198, 'Theo tín hiệu đèn của xe cơ giới, xe nào vi phạm quy tắc giao thông?', 'Xe mô tô.', 'Xe ô tô con. ', 'Không xe nào vi phạm. ', 'Cả hai xe.', 4, 0, '559.jpg', 'Biển số R.301a chỉ cho phép các phương tiện đi thẳng trong khi cả 2 xe đều có tín hiệu xe nhan rẽ sang hướng khách nên cả 2 xe đều vi phạm quy tắc giao thông.\r\n', 'sahinh', 'A1', 6),
(199, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe con. ', 'Xe tải.', 'Xe con, xe tải.', '', 2, 0, '560.jpg', 'Xe con trong cả 2 phía đều đang đúng làn đường và đi theo hướng mà đèn xanh đang bật nên đúng quy tắc giao thông.\r\nXe tải trong cả 2 phía đều ở sai làn đường so với hướng rẽ nên vi phạm quy tắc giao thông.\r\n', 'sahinh', 'A1', 7),
(200, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe tải, xe con. ', 'Xe khách, xe con.', 'Xe khách, xe tải.', '', 3, 0, '561.jpg', 'Xe con và xe mô tô đang ở đúng làn đường, đúng hướng rẽ với tín hiệu đèn xanh nên đúng quy tắc giao thông.\r\nXe khách và xe tải đang ở sai làn đường so với hướng rẽ nên đều vi phạm.\r\n', 'sahinh', 'A1', 8),
(201, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe con, xe tải, xe khách.', 'Xe tải, xe khách, xe mô tô. ', 'Xe khách, xe mô tô, xe con.', 'Cả bốn xe.', 2, 0, '562.jpg', 'Xe con đi đúng làn đường theo hướng rẽ và tín hiệu đèn.\r\nXe khách: Sai làn đường và tín hiệu đèn.\r\nXe tải: Đi thẳng vi phạm đèn đỏ.\r\nXe mô tô: Đi thẳng vi phạm đèn đỏ.\r\n', 'sahinh', 'A1', 1),
(202, 'Các xe đi theo hướng mũi tên, xe nào chấp hành đúng quy tắc giao thông?', 'Xe tải, mô tô.', 'Xe khách, mô tô. ', 'Xe tải, xe con.', 'Mô tô, xe con.', 2, 0, '565.jpg', 'Xe con và xe tải: Vi phạm tín hiệu đèn;\r\nXe khách và xe mô tô: Đúng làn đường và đúng tín hiệu đèn.\r\n', 'sahinh', 'A1', 2),
(203, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con. ', 'Xe con, xe của bạn, mô tô, ', 'Mô tô, xe con, xe của bạn.', '', 3, 0, '567.jpg', 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Đường cùng cấp: Bên phải trống, rẽ phải, đi thẳng, rẽ trái.\r\nBiển báo phía trước là đường ưu tiên và biển phụ báo hướng di chuyển của xe mô tô là hướng ưu tiên.\r\n1. Xe mô tô: Đường ưu tiên;\r\n2. Xe con: Đường không ưu tiên, bên phải trống;\r\n3. Xe của bạn: Đường không ưu tiên, bên phải vướng xe con.\r\n', 'sahinh', 'A1', 3),
(204, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con. ', 'Xe con, xe của bạn, mô tô. ', 'Mô tô, xe con, xe của bạn.', '', 2, 0, '568.jpg', 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Đường cùng cấp: Bên phải trống, rẽ phải, đi thẳng, rẽ trái.\r\n1. Xe con: Rẽ phải; 2: Xe của bạn: Đi thẳng; 3. Mô tô: Rẽ trái.\r\n', 'sahinh', 'A1', 4),
(205, 'Bạn xử lý như thế nào trong trường hợp này?', 'Tăng tốc độ, rẽ phải trước xe tải và xe đạp. ', 'Giảm tốc độ, rẽ phải sau xe tải và xe đạp. ', 'Tăng tốc độ, rẽ phải trước xe đạp.', '', 2, 0, '572.jpg', 'Xe tải đã vào nơi giao nhau trước nên sẽ được ưu tiên trước. Biển báo phía trước là đường ưu tiên dành cho xe thô sơ nên phải nhường đường cho xe đạp. Do đó, xe bạn rẽ phải sau xe tải và xe đạp.\r\n', 'sahinh', 'A1', 5),
(206, 'Xe nào dừng đúng theo quy tắc giao thông?', 'Xe con. ', 'Xe mô tô.', 'Cả 2 xe đều đúng.', '', 1, 0, '592.jpg', 'Biển số W.211a “Giao nhau với đường sắt không có rào chắn” thì các phương tiện đứng cách xa đường sắt tối thiểu 5m nên xe con đúng.\r\n', 'sahinh', 'A1', 6),
(207, 'Xe của bạn đang di chuyển gần đến khu vực giao cắt với đường sắt, khi rào chắn đang dịch chuyển, bạn điều khiển xe như thế nào là đúng quy tắc giao thông?', 'Quan sát nếu thấy không có tầu thì tăng tốc cho xe vượt qua đường sắt. ', 'Dừng lại trước rào chắn một khoảng cách an toàn. ', 'Ra tín hiệu, yêu cầu người gác chắn tàu kéo chậm Barie để xe bạn qua.', '', 2, 0, '596.jpg', 'Bắt buộc phải dừng xe.\r\n', 'sahinh', 'A1', 7),
(208, 'Trong tình huống dưới đây, xe đầu kéo kéo rơ moóc (xe container) đang rẽ phải, xe con màu xanh và xe máy phía sau xe container đi như thế nào để đảm bảo an toàn?', 'Vượt về phía bên phải để đi tiếp. ', 'Giảm tốc độ chờ xe container rẽ xong rồi tiếp tục đi. ', 'Vượt về phía bên trái để đi tiếp.', '', 2, 0, '600.jpg', 'Giảm tốc độc chờ xe đầu kéo rẽ phải rồi mới tiếp tục đi.\r\n', 'sahinh', 'A1', 8),
(309, 'Phần của đường bộ được sử dụng cho các phương tiện giao thông qua lại là gì?', 'Phần mặt đường và lề đường. ', 'Phần đường xe chạy.', 'Phần đường xe cơ giới.', '', 2, 0, '', 'Phần đường xe chạy là phần của đường bộ được sử dụng cho phương tiện giao thông qua lại.', 'luat', 'A2', 1),
(310, '“Cuộc đua xe chỉ được thực hiện khi nào?\"', 'Diễn ra trên đường phố không có người qua lại.', 'Được người dân ủng hộ.', 'Được cơ quan có thẩm quyền cấp phép', '', 3, 0, '', 'Giải đua xe phải do Sở Văn Hóa và Thể Thao cấp phép mới được tổ chức - Cơ quan có thẩm quyền.', 'luat', 'A2', 1),
(311, 'Ở phần đường dành cho người đi bộ qua đường, trên cầu, đầu cầu, đường cao tốc, đường hẹp, đường dốc, tại nơi đường bộ giao nhau cùng mức với đường sắt có được quay đầu xe hay không?', 'Được phép.', 'Không được phép.', 'Tùy từng trường hợp.', '', 2, 1, '', '“KHÔNG ĐƯỢC PHÉP” quay đầu xe trên: đường cao tốc, đường dốc, đường hẹp...', 'luat', 'A2', 1),
(312, 'Người có GPLX mô tô hạng A1 không được phép điều khiển loại xe nào dưới đây?', 'Xe mô tô có dung tích xi-lanh 125 cm3.', 'Xe mô tô có dung tích xi-lanh từ 175 cm3 trở lên. ', 'Xe mô tô có dung tích xi-lanh 100 cm3.', '', 2, 0, '', 'GPLX hạng A1 được chạy xe từ 50 cm3 – dưới 175 cm3. GPLX hạng A2 được chạy xe trên 175 cm3. Vì vậy chọn Ý 2.', 'luat', 'A2', 1),
(313, 'Khi điều khiển xe chạy trên đường biết có xe sau xin vượt nếu đủ điều kiện an toàn người lái xe phải làm gì?', 'Tăng tốc độ và ra hiệu cho xe sau vượt, không được gây trở ngại cho xe sau vượt.', 'Người điều khiển phương tiện phía trước phải giảm tốc độ, đi sát về bên phải của phần đường xe chạy cho đến khi xe sau đã vượt qua, không được gây trở ngại cho xe sau vượt.', 'Cho xe tránh về bên trái mình và ra hiệu cho xe sau vượt; nếu có chướng ngại vật phía trước hoặc thiếu điều kiện an toàn chưa cho vượt được phải ra hiệu cho xe sau biết; cấm gây trở ngại cho xe xin vượt.', '', 2, 0, '', 'Xe sau xin vượt thì: Phải giảm tốc độ và đi sát lề đường bên phải, không gây cản trở.', 'luat', 'A2', 1),
(314, 'Người điều khiển xe mô tô hai bánh, xe gắn máy có được đi xe dàn hàng ngang; đi xe vào phần đường dành cho người đi bộ và phương tiện khác; sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính) hay không ?', 'Được phép nhưng phải đảm bảo an toàn. ', 'Không được phép.', 'Được phép tùy từng hoàn cảnh, điều kiện cụ thể.', '', 2, 1, '', 'Hành vi nghiêm cấm: đi xe dàn hàng ngang, sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính).', 'luat', 'A2', 1),
(315, 'Trên đường bộ ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 60 km/h?', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, ô tô trộn vữa, ô tô trộn bê tông, ô tô xi téc.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc bằng 3,5 tấn.', '', 1, 0, '', 'Đường đôi có giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 60 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 70 km/h: Xe MÔTÔ, ÔTÔ Buýt. 80 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 90 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải dưới 3,5 tấn.', 'luat', 'A2', 1),
(316, 'Khi xe ô tô, mô tô đến gần vị trí giao nhau giữa đường bộ và đường sắt không có rào chắn, khi đèn tín hiệu màu đỏ đã bật sáng hoặc khi có tiếng chuông báo hiệu, người lái xe xử lý như thế nào?', 'Giảm tốc độ cho xe vượt qua đường sắt.', 'Nhanh chóng cho xe vượt qua đường sắt trước khi tàu hỏa tới.', 'Giảm tốc độ cho xe vượt qua đường sắt trước khi tàu hỏa tới. ', 'Cho xe dừng ngay lại và giữ khoảng cách tối thiểu 5 mét tính từ ray gấn nhất.', 4, 0, '', 'Khi có xe tàu hỏa chạy qua, dừng lại cách đường ray gần nhất ít nhất 5 mét.', 'luat', 'A2', 1),
(317, 'Trên làn đường dành cho ô tô có vũng nước lớn, có nhiều người đi xe mô tô trên làn đường bên cạnh, người lái xe ô tô xử lý như thế nào là có văn hóa giao thông?', 'Cho xe chạy thật nhanh qua vũng nước.', 'Giảm tốc độ cho xe chạy chậm qua vũng nước.', 'Giảm tốc độ cho xe chạy qua làn đường dành cho mô tô để tránh vũng nước.', '', 2, 0, '', 'Gặp vũng nước thì phải chạy chậm để không văng nước vào xe đi trong lề.', 'luat', 'A2', 1),
(318, 'Khi tránh nhau trên đường hẹp, người lái xe cần phải chú ý những điểm nào để đảm bảo an toàn giao thông?', 'Không nên đi cố vào đường hẹp; xe đi ở sườn núi nên dừng lại trước để nhường đường; khi dừng xe nhường đường phải đỗ ngay ngắn.', 'Trong khi tránh nhau không nên đổi số; khi tránh nhau ban đêm, phải tắt đèn pha bật đèn cốt.', 'Khi tránh nhau ban đêm, phải thường xuyên bật đèn pha tắt đèn cốt.', 'Cả ý 1 và ý 2.', 4, 0, '', 'Ý 1: Phải nhường đường là đúng Ý 2: Tránh xe ban đêm thì bật đèn cốt là đúng Ý 3: Tránh xe ban đêm bật đèn pha là sai. Vì vậy chọn Ý 1 và Ý 2.', 'luat', 'A2', 1),
(319, 'Thế nào là động cơ 4 kỳ?', 'Là loại động cơ: Để hoàn thành một chu trình công tác của động cơ, pít tông thực hiện 2 (hai) hành trình, trong đó có một lần sinh công.', 'Là loại động cơ: Để hoàn thành một chu trình công tác của động cơ, pít tông thực hiện 4 (bốn) hành trình, trong đó có một lần sinh công.', '', '', 2, 0, '', 'Động cơ 4 kỳ là động cơ có pít tông thực hiện 4 hành trình sinh công.', 'luat', 'A2', 1),
(320, 'Biển nào cấm xe rẽ trái?', 'Biển 1.', 'Biển 2', 'Cả hai biển.', '', 1, 0, '318.jpg', 'Biển 1: Cấm rẽ trái. Không cấm quay đầu. Biển 2: Cấm quay đầu. Không cấm rẽ trái. Vì vậy chọn biển 1.', 'bienbao', 'A2', 1),
(321, 'Gặp biển nào xe xích lô được phép đi vào?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 1 và biển 2.', 4, 0, '336.jpg', 'Biển 1: Cấm xe lam. Biển 2: Cấm xe xích lô máy. Biển 3: Cấm xe xích lô. Biển 1 và Biển 2 cấm xe lam và xích lô máy không cấm xích lô nên xích lô được phép đi.', 'bienbao', 'A2', 1),
(322, 'Biển nào xe mô tô hai bánh không được đi vào?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '354.jpg', 'Biển 1: Cấm ô tô không cấm xe mô tô. Biển 2: Cấm xe mô tô hai bánh. Biển 3: Cấm xe tải không cấm xe mô tô. Vì vậy chọn Biển 2.', 'bienbao', 'A2', 1),
(323, 'Biển nào chỉ đường dành cho người đi bộ, các loại xe không được đi vào khi gặp biển này?', 'Biển 1.', 'Biển 1 và 3.', 'Biển 3', 'Cả 3 Biển', 3, 0, '372.jpg', 'Biển 1: Báo hiệu nhường đường cho người đi bộ. Biển 2: Cấm người đi bộ vào. Biển 3: Đường này chỉ dành cho người đi bộ. Vì vậy chọn biển 3.', 'bienbao', 'A2', 1),
(324, 'Biển nào báo hiệu “Giao nhau với đường ưu tiên”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 3, 0, '390.jpg', 'Biển 1: Giao nhau với đường cùng cấp (Cùng Cấp dấu Cộng) Biển 2: Giao nhau với đường không ưu tiên, đi trên đường này được quyền ưu tiên đi trước. Biển 3: Giao nhau với đường ưu tiên, gặp biển này phải nhường đường', 'bienbao', 'A2', 1),
(325, 'Biển nào sau đây là biển “Kè, vực sâu bên đường phía bên trái”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 1 Và Biển 2.', 3, 0, '408.jpg', 'Biển 1: Kè, vực sâu phía trước. Biển 2: Kè, vực sâu bên đường phía bên phải. Có hình hai bánh xe bên tay phải sắp rơi xuống kè, vực bên phải. Biển 3: Kè, vực sâu bên đường phái bên trái. Có hình hai bánh xe bên tay trái sắp rơi xuống kè, vực bên trái.', 'bienbao', 'A2', 1),
(326, 'Biển báo này có ý nghĩa gì?', 'Để báo trước gần tới đoạn đường có hiện tượng đất đá từ trên ta luy dương sụt lở bất ngờ gây nguy hiểm cho xe cộ và người đi đường.', 'Để báo trước nơi có kết cấu mặt đường rời rạc, khi phương tiện đi qua, làm cho các viên đá, sỏi băng lên gây nguy hiểm và mất an toàn cho người và phương tiện tham gia giao thông.', 'Để cảnh báo những đoạn nền đường yếu, đoạn đường đang theo dõi lún mà việc vận hành xe ở tốc độ cao có thể gây nguy hiểm.', '', 1, 0, '426.jpg', 'Biển 1 và Biển 2: Đá lở. Có hình đá rơi từ trên núi xuống nên tìm đáp án có chữ đất đá!', 'bienbao', 'A2', 1),
(327, 'Biển nào cho phép được quay đầu xe đi theo hướng ngược lại khi đặt biển trước ngã ba, ngã tư?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Không biển nào.', 3, 0, '444.jpg', 'Biển 1: Đặt trước ngã 3, ngã 4, chỉ được rẽ trái ở phạm vi ngã 3 ngã 4 phía sau mặt biển. Biển 2: Đặt phía sau ngã 3, 4 có hiệu lực phía trước mặt biển. Gặp biển này chỉ được rẽ trái hoặc phải. Biển 3: Đặt trước ngã 3, ngã tư được phép đi thẳng hoặc rẽ trái phía sau mặt biển và được quay đầu xe để đi theo hướng ngược lại. Vì vậy chọn Biển 3.', 'bienbao', 'A2', 1),
(328, 'Biển số 1 có ý nghĩa gì?', 'Đi thẳng hoặc rẽ trái trên cầu vượt.', 'Đi thẳng hoặc rẽ phải trên cầu vượt.', 'Báo hiệu cầu vượt liên thông.', '', 3, 0, '462.jpg', 'Biển 1 báo hiệu cầu vượt liên thông Biển 2 và 3 báo hiệu cầu vượt cắt ngang.', 'bienbao', 'A2', 1),
(329, 'Vạch kẻ đường nào dưới đây là vạch phân chia hai chiều xe chạy (vạch tim đường)?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Vạch 1 và vạch 3.', 4, 0, '480.jpg', 'Vạch trắng phân chia cùng chiều, nhiều làn xe Vạch vàng phân chia hai chiều xe chạy.', 'sahinh', 'A2', 1),
(330, 'Theo tín hiệu đèn, xe tải đi theo hướng nào là đúng quy tắc giao thông?', 'Hướng 2, 3, 4.', 'Chỉ hướng 1.', 'Hướng 1 và 2.', 'Hướng 3 và 4.', 2, 0, '502.jpg', 'Theo tín hiệu đèn: Hướng 1 đèn xanh rẽ phải nên được phép đi. Hướng đi thẳng đèn tín hiệu đỏ do đó các hướng 2, 3, 4 không được đi. Vì vậy chỉ hướng 1 được đi.', 'sahinh', 'A2', 1),
(331, 'Các xe đi như thế nào là đúng quy tắc giao thông?', 'Các xe ở phía tay phải và tay trái của người điều khiển được phép đi thẳng.', 'Cho phép các xe ở mọi hướng được rẽ phải.', 'Tất cả các xe phải dừng lại trước ngã tư, trừ những xe đã ở trong ngã tư được phép tiếp tục đi.', '', 3, 0, '528.jpg', 'Cảnh sát giao thông giơ tay thẳng đứng nghĩa là tất cả các xe phải dừng lại trừ xe đã vào ngã 3, 4', 'sahinh', 'A2', 1),
(332, 'Xe nào đi trước là đúng quy tắc giao thông?', 'Xe của bạn.', 'Xe tải. ', '', '', 2, 0, '549.jpg', 'Biển báo phía trước là biển giao nhau với đường ưu tiên. Xe tải đang đi trên đường ưu tiên, xe của bạn đi trên đường không ưu tiên. Vì vậy phải nhường xe tải đi trước.', 'sahinh', 'A2', 1),
(333, 'Bạn xử lý như thế nào trong trường hợp này?', 'Tăng tốc độ, rẽ phải trước xe tải và xe đạp. ', 'Giảm tốc độ, rẽ phải sau xe tải và xe đạp. ', 'Tăng tốc độ, rẽ phải trước xe đạp.', '', 2, 0, '572.jpg', 'Trên hình xe tải đã vào ngã tư trước nên ưu tiên đi trước. Biển báo phải trước là đường ưu tiên dành cho xe thô sơ nên nhường đường cho xe đạp.Vì vậy xe bạn giảm tốc độ và rẽ phải sau cùng.', 'sahinh', 'A2', 1),
(334, '“Làn đường” là gì?', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, sử dụng cho xe chạy.', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn.', 'Là đường cho xe ô tô chạy, dừng, đỗ an toàn.', '', 2, 0, '', 'Làn đường được chia theo chiều dọc, có bề rộng đủ cho xe chạy an toàn.', 'luat', 'A2', 2),
(335, '“Người điều khiển phương tiện giao thông đường bộ mà trong cơ thể có chất ma tuý có bị nghiêm cấm hay không?', 'Bị nghiêm cấm.', 'Không bị nghiêm cấm.', 'Không bị nghiêm cấm, nếu có chất ma tuý ở mức nhẹ, có thể điều khiển phương tiện tham gia giao thông.', '', 1, 1, '', 'Có chất ma túy đều bị nghiêm cấm.', 'luat', 'A2', 2),
(336, 'Bạn đang lái xe phía trước có một xe cảnh sát giao thông không phát tín hiệu ưu tiên bạn có được phép vượt hay không?', 'Không được vượt.', 'Được vượt khi đang đi trên cầu.', 'Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', 'Được vượt khi đảm bảo an toàn.', 4, 0, '', 'Xe CSGT không bật còi hú ưu tiên thì vẫn được vượt khi thấy an toàn.', 'luat', 'A2', 2),
(337, 'Người có GPLX mô tô hạng A1 được phép điều khiển loại xe nào dưới đây?', 'Xe mô tô hai bánh có dung tích xi-lanh từ 50 cm3 đến dưới 175 cm3.', 'Xe mô tô ba bánh dùng cho người khuyết tật. ', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'GPLX hạng A1 được chạy xe từ 50 cm3 – dưới 175 cm3 và cả xe 3 bánh cho người khuyết tật. Cả 2 Ý là đúng.', 'luat', 'A2', 2),
(338, 'Trong khu dân cư, ở nơi nào cho phép người lái xe, người điều khiển xe máy chuyên dùng được quay đầu xe?', 'Ở nơi đường giao nhau và nơi có biển báo cho phép quay đầu xe.', 'Ở nơi có đường rộng để cho các loại xe chạy một chiều.', 'Ở bất kỳ nơi nào.', '', 1, 0, '', 'Được quay đầu xe ở nơi giao nhau và có biển báo quay đầu.', 'luat', 'A2', 2),
(339, 'Người lái xe phải giảm tốc độ thấp hơn tốc độ tối đa cho phép (có thể dừng lại một cách an toàn) trong trường hợp nào dưới đây?', 'Khi có báo hiệu cảnh báo nguy hiểm hoặc có chướng ngại vật trên đường; khi chuyển hướng xe chạy hoặc tầm nhìn bị hạn chế; khi qua nơi đường giao nhau, nơi đường bộ giao nhau với đường sắt; đường vòng; đường có địa hình quanh co, đèo dốc.', 'Khi qua cầu, cống hẹp; khi lên gần đỉnh dốc, khi xuống dốc, khi qua trường học, khu đông dân cư, khu vực đang thi công trên đường bộ; hiện trường xảy ra tai nạn giao thông.', 'Khi điều khiển xe vượt xe khác trên đường quốc lộ, đường cao tốc.', 'Cả ý 1 và ý 2.', 4, 0, '', 'Gặp biển báo hiệu nguy hiểm thì phải giảm tốc độ, khi hiện trường xảy ra tai nạn giao thông, qua cầu, cống hẹp đều giảm tốc độ.', 'luat', 'A2', 2),
(340, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 80 km/h?', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc bằng 3,5 tấn.', '', 3, 0, '', 'Đường hai chiều không có giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 50 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 60 km/h: Xe MÔTÔ, ÔTÔ Buýt. 70 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 80 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải dưới 3,5 tấn.', 'luat', 'A2', 2),
(341, 'Khi điều khiển xe trên đường vòng, khuất tầm nhìn người lái xe cần phải làm gì để đảm bảo an toàn?', 'Đi đúng làn đường, đúng tốc độ quy định, không được vượt xe khác.', 'Đi sang làn đường của xe ngược chiều để mở rộng tầm nhìn và vượt xe khác.', 'Cho xe đi sát bên phải làn đường, bật tín hiệu báo hiệu để vượt bên phải xe khác.', '', 1, 0, '', 'Đường vòng khuất tầm nhìn thì ta phải giảm tốc độ, báo hiệu bằng đèn còi, đi đúng làn đường của mình và không được vượt.', 'luat', 'A2', 2),
(342, 'Người lái xe cố tình không phân biệt làn đường, vạch phân làn, phóng nhanh, vượt ẩu, vượt đèn đỏ, đi vào đường cấm, đường một chiều được coi là hành vi nào trong các hành vi dưới đây?', 'Là bình thường.', 'Là thiếu văn hóa giao thông. ', 'Là có văn hóa giao thông.', '', 2, 1, '', 'Thiếu văn hóa giao thông', 'luat', 'A2', 2),
(343, 'Khi điều khiển xe trên đường vòng người lái xe cần phải làm gì để đảm bảo an toàn?', 'Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; giảm tốc độ tới mức cần thiết, về số thấp và thực hiện quay vòng với tốc độ phù hợp với bán kính cong của đường vòng.', 'Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; tăng tốc để nhanh chóng qua đường vòng, đạp ly hợp (côn) và giảm tốc độ sau khi qua đường vòng.', '', '', 1, 0, '', 'Đường vòng khuất tầm nhìn thì ta phải giảm tốc độ, báo hiệu bằng đèn còi, đi đúng làn đường của mình và không được vượt.', 'luat', 'A2', 2),
(344, 'Đèn phanh trên xe mô tô có tác dụng gì dưới đây?', 'Có tác dụng cảnh báo cho các xe phía sau biết xe đang giảm tốc để chủ động tránh hoặc giảm tốc để tránh va chạm.', 'Có tác dụng định vị vào ban đêm với các xe từ phía sau tới để tránh va chạm.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Đèn phanh/ Đèn thắng vừa để giảm tốc độ vừa để cho xe sau định vị khoảng cách giữa 2 xe khi xe trước thắng lại.', 'luat', 'A2', 2),
(345, 'Khi gặp biển nào xe được rẽ trái?', 'Biển 1.', 'Biển 2.', 'Không biển nào.', '', 2, 0, '319.jpg', 'Luật mới Biển 1: Cấm rẽ trái, không cấm quay đầu. Biển 2: Cấm quay đầu, không cấm rẽ trái.', 'bienbao', 'A2', 2),
(346, 'Gặp biển nào xe lam, xe xích lô máy được phép đi vào?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 3, 0, '337.jpg', 'Biển 1: Cấm xe lam. Biển 2: Cấm xe xích lô máy. Biển 3: Cấm xe xích lô, không cấm xe lam và xe xích lô máy. Vì vậy khi gặp BIỂN 3 xe làm và xích lô máy được phép đi.', 'bienbao', 'A2', 2),
(347, 'Ba biển này có hiệu lực như thế nào?', 'Cấm các loại xe ở biển phụ đi vào.', 'Cấm các loại xe cơ giới đi vào trừ loại xe ở biển phụ.', '', '', 1, 0, '355.jpg', 'Biển chính là biển báo ĐƯỜNG CẤM, có biển phụ thì áp dụng cấm theo biển phụ.', 'bienbao', 'A2', 2),
(348, 'Biển nào báo hiệu “Đường dành cho xe thô sơ”?', 'Biển 1.', 'Biển 2.', 'Biển 3', '', 1, 0, '373.jpg', 'Biển 1: Đường chỉ dành cho xe thô sơ Biển 2: Cấm xe đạp đi vào Biển 3: Báo hiệu đường người đi xe đạp cắt ngang. Vì vậy chọn biển 1.', 'bienbao', 'A2', 2),
(349, 'Biển nào báo hiệu “Đường giao nhau” của các tuyến đường cùng cấp?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 1, 0, '391.jpg', 'Biển 1: Giao nhau với đường cùng cấp (Cùng Cấp dấu Cộng) Biển 2: Giao nhau với đường không ưu tiên, đi trên đường này được quyền ưu tiên đi trước. Biển 3: Giao nhau với đường ưu tiên, gặp biển này phải nhường đường. Vì vậy chọn Biển 1.', 'bienbao', 'A2', 2),
(350, 'Biển nào sau đây là biển “Kè, vực sâu bên đường phía bên phải”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '409.jpg', 'Biển 1: Kè, vực sâu phía trước. Biển 2: Kè, vực sâu bên đường phía bên phải. Có hình hai bánh xe bên tay phải sắp rơi xuống kè, vực bên phải. Biển 3: Kè, vực sâu bên đường phái bên trái. Có hình hai bánh xe bên tay trái sắp rơi xuống kè, vực bên trái. Vì vậy chọn Biển 2.', 'bienbao', 'A2', 2),
(351, 'Biển báo này có ý nghĩa gì?', 'Để báo trước gần tới đoạn đường có hiện tượng đất đá từ trên ta luy dương sụt lở bất ngờ gây nguy hiểm cho xe cộ và người đi đường.', 'Để báo trước nơi có kết cấu mặt đường rời rạc, khi phương tiện đi qua, làm cho các viên đá, sỏi băng lên gây nguy hiểm và mất an toàn cho người và phương tiện tham gia giao thông.', 'Để cảnh báo những đoạn nền đường yếu, đoạn đường đang theo dõi lún mà việc vận hành xe ở tốc độ cao có thể gây nguy hiểm.', '', 2, 0, '427.jpg', 'Biển báo: sỏi đá bắn lên. Biển báo giao thông để báo trước nơi có kết cấu mặt đường rời rạc, khi phương tiện đi qua, làm cho các viên đá, sỏi băng lên gây nguy hiểm và mất an toàn cho người và phương tiện tham gia giao thông. !', 'bienbao', 'A2', 2),
(352, 'Biển nào không cho phép rẽ phải?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 1 và biển 3.', 1, 0, '445.jpg', 'Biển 1: Chỉ được rẽ trái. Biển 2: Đặt sau ngã 3, 4 chỉ được rẽ trái hoặc phải Biển 3: Chỉ được đi thẳng hoặc rẽ phải. Vì vậy chọn Biển 1.', 'bienbao', 'A2', 2),
(353, 'Biển nào báo hiệu “Tuyến đường cầu vượt cắt qua”?', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2 và 3.', '', 1, 0, '463.jpg', 'Biển 1 và 2: Báo hiệu tuyến đường có cầu vượt cắt qua Biển 3: Báo hiệu sắp đi qua cầu vòng lớn. Vì vậy chọn biển 1 và biển 2.', 'bienbao', 'A2', 2),
(354, 'Các vạch dưới đây có tác dụng gì?', 'Phân chia hai chiều xe chạy ngược chiều nhau.', 'Phân chia các làn xe chạy cùng chiều nhau.', '', '', 1, 0, '481.jpg', 'Vạch màu vàng phân chia hai chiều xe chạy ngược nhau. CòN vạch trắng phân chia các làn xe chạy cùng chiều.', 'sahinh', 'A2', 2),
(355, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe khách, xe tải, mô tô. ', 'Xe tải, xe con, mô tô.', 'Xe khách, xe con, mô tô.', '', 1, 0, '503.jpg', 'Mũi tên đỏ là hướng xe đang di chuyển. Theo tín hiệu đèn: đèn xanh ô tô rẽ phải đúng.Đèn đỏ xe tải đi là sai. Xe khách ở làn bắt buộc rẽ trái mà đi thẳng là sai. Mô Tô làn bắt buộc rẽ trái mà rẽ phải sai. Vì vậy xe vi phạm: khách, tải, mô tô.', 'sahinh', 'A2', 2),
(356, 'Theo hướng mũi tên, xe nào được phép đi?', 'Mô tô, xe con.', 'Xe con, xe tải.', 'Mô tô, xe tải.', 'Cả ba xe', 3, 0, '529.jpg', 'CSGT giơ 2 tay sang ngang: Phía trước mặt và sau CSGT dừng lại, Phía bên trái phải đi. Nên: Xe con đứng lại, Xe mô tô và xe tải được đi.', 'sahinh', 'A2', 2),
(357, 'Người lái xe có thể quay đầu xe như thế nào là đúng quy tắc giao thông?', 'Quay đầu theo hướng A. ', 'Quay đầu theo hướng B. ', 'Cấm quay đầu.', '', 1, 0, '550.jpg', 'Biển báo cấm rẽ trái nhưng không cấm quay đầu. Hướng A: được quay xe vì: không cán vạch dành cho người đi bộ, trong giao lộ được quay đầu. Hướng B: Còn dính vạch liền nên không được quay xe.', 'sahinh', 'A2', 2),
(358, 'Bạn xử lý như thế nào trong trường hợp này?', 'Tăng tốc độ, rẽ phải trước xe con màu xanh phía trước và người đi bộ.', 'Giảm tốc độ, để người đi bộ qua đường và rẽ phải trước xe con màu xanh.', 'Giảm tốc độ, để người đi bộ qua đường và rẽ phải sau xe con màu xanh.', '', 3, 0, '573.jpg', 'Gặp người đi bộ thì giảm tốc độ phải nhường đường cho người đi bộ, xe con màu xanh đang trong giao lộ nên được quyền rẽ phải đi trước.', 'sahinh', 'A2', 2),
(359, 'Khái niệm “khổ giới hạn của đường bộ” được hiểu như thế nào là đúng?', 'Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, bến phà, hầm đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.', 'Là khoảng trống có kích thước giới hạn về chiều rộng của đường, cầu, bến phà, hầm trên đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.', 'Là khoảng trống có kích thước giới hạn về chiều cao của cầu, bến phà, hầm trên đường bộ để các xe đi qua được an toàn.', '', 1, 0, '', 'Khổ giới hạn là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng.', 'luat', 'A2', 3),
(360, 'Việc lái xe mô tô, ô tô, máy kéo ngay sau khi uống rượu, bia có được phép hay không?', 'Không được phép.', 'Chỉ được lái ở tốc độ chậm và quãng đường ngắn. ', 'Chỉ được lái nếu trong cơ thể có nồng độ cồn thấp.', '', 1, 1, '', 'Uống rượu bia không được lái xe.', 'luat', 'A2', 3),
(361, 'Bạn đang lái xe phía trước có một xe cứu thương đang phát tín hiệu ưu tiên bạn có được phép vượt hay không?', 'Không được vượt.', 'Được vượt khi đang đi trên cầu.', 'Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', 'Được vượt khi đảm bảo an toàn.', 1, 0, '', 'Xe cứu thương đang phát tín hiệu ưu tiên thì không được vượt.', 'luat', 'A2', 3),
(362, 'Người có GPLX mô tô hạng A2 được phép điều khiển loại xe nào dưới đây?', 'Xe mô tô ba bánh.', 'Xe mô tô hai bánh có dung tích xi-lanh từ 175 cm3 trở lên và các loại xe quy định cho giấy phép lái xe hạng A1.', 'Các loại máy kéo nhỏ có trọng tải đến 1.000 kg.', '', 2, 0, '', 'GPLX A2 điều khiển: Xe mô tô hai bánh có dung tích xi-lanh từ 175 cm3 trở lên và các loại xe cho GPLX hạng A1.', 'luat', 'A2', 3),
(363, 'Người lái xe phải làm gì khi quay đầu xe trên cầu, đường ngầm hay khu vực đường bộ giao nhau cùng mức với đường sắt?', 'Không được quay đầu xe.', 'Lợi dụng chỗ rộng và phải có người làm tín hiệu sau xe để bảo đảm an toàn.', 'Lợi dụng chỗ rộng có thể quay đầu được để quay đầu xe cho an toàn.', '', 1, 1, '', 'Không được quay đầu xe: trên cầu, đường ngầm, giao nhau với đường sắt.', 'luat', 'A2', 3),
(364, 'Khi gặp một đoàn xe, một đoàn xe tang hay gặp một đoàn người có tổ chức theo đội ngũ, người lái xe phải xử lý như thế nào?', 'Từ từ đi cắt qua đoàn người, đoàn xe.', 'Không được cắt ngang qua đoàn người, đoàn xe.', 'Báo hiệu từ từ cho xe đi cắt qua để bảo đảm an toàn.', '', 2, 1, '', 'Nguyên nhân phải nhường: Đoàn xe tang nằm trong các xe ưu tiên, dưới xe Hộ Đê.', 'luat', 'A2', 3),
(365, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có 01 làn xe cơ giới (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 70 km/h?', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn (trừ ô tô xi téc).', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc bằng 3,5 tấn.', '', 2, 0, '', 'Đường hai chiều KHÔNG CÓ giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 50 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 60 km/h: Xe MÔTÔ, ÔTÔ Buýt. 70 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 80 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải dưới 3,5 tấn.', 'luat', 'A2', 3),
(366, 'Người lái xe phải xử lý như thế nào khi quan sát phía trước thấy người đi bộ đang sang đường tại nơi có vạch đường dành cho người đi bộ để đảm bảo an toàn?', 'Giảm tốc độ, đi từ từ để vượt qua trước người đi bộ.', 'Giảm tốc độ, có thể dừng lại nếu cần thiết trước vạch dừng xe để nhường đường cho người đi bộ qua đường.', 'Tăng tốc độ để vượt qua trước người đi bộ.', '', 2, 1, '', 'Gặp người đi bộ sang đường mình phải giảm tốc độ và dừng lại.', 'luat', 'A2', 3),
(367, 'Khi sơ cứu người bị tai nạn giao thông đường bộ, có vết thương chảy máu ngoài, màu đỏ tươi phun thành tia và phun mạnh khi mạch đập, bạn phải làm gì dưới đây?', 'Thực hiện cầm máu trực tiếp.', 'Thực hiện cầm máu không trực tiếp (chặn động mạch).', '', '', 2, 0, '', 'Máu phun thành tia thì chặn động mạch.', 'luat', 'A2', 3),
(368, 'Khi điều khiển xe qua đường sắt, người lái xe cần phải thực hiện các thao tác nào dưới đây để đảm bảo an toàn?', 'Khi có chuông báo hoặc thanh chắn đã hạ xuống, người lái xe phải dừng xe tạm thời đúng khoảng cách an toàn, kéo phanh tay nếu đường dốc hoặc phải chờ lâu.', 'Khi không có chuông báo hoặc thanh chắn không hạ xuống, người lái xe phải quan sát nếu thấy đủ điều kiện an toàn thì về số thấp, tăng ga nhẹ và không thay đổi số trong quá trình vượt qua đường sắt để tránh động cơ chết máy cho xe vượt qua.', 'Cả ý 1 và ý 2.', '', 3, 1, '', 'Ý 1: Khi có chuông báo, thanh chắn hạ xuống thì phải dừng lại. Ý 2: Khi không có chuông báo vê số thấp tăng ga nhẹ để tránh tắt máy. Vì vậy chọn Ý 1 và Ý 2.', 'luat', 'A2', 3),
(369, 'Trên xe ô tô có trang bị thiết bị như hình vẽ có tác dụng gì dưới đây?', 'Dùng để kích (hay nâng) xe ô tô.', 'Vặn ốc lắp bánh xe.', 'Ổn định chuyển động của xe ô tô khi đi vào đường vòng.', 'Giữ chặt người lái và hành khách trên ghế ngồi khi xe ô tô đột ngột dừng lại.', 1, 0, '302.jpg', 'Công dụng để dùng để kích (hay nâng) xe ô tô.', 'luat', 'A2', 3),
(370, 'Biển nào cấm ô tô rẽ trái?', 'Biển 1.', 'Biển 2.', 'Cả hai biển', '', 3, 0, '320.jpg', 'Biển 1: Cấm tất cả các xe rẽ trái, Không cấm quay đầu Biển 2: Cấm ô tô rẽ trái hoặc quay đầu. Vì vậy chọn cả 2 biển.', 'bienbao', 'A2', 3),
(371, 'Biển báo này có ý nghĩa gì?', 'Đường cấm súc vật vận tải hàng hóa.', 'Đường cấm súc vật vận tải hành khách.', 'Đường cấm súc vật vận tải hàng hóa hoặc hành khách dù kéo xe hay chở trên lưng đi qua.', '', 3, 0, '338.jpg', 'Cấm kéo xe súc vật chở người hoặc hàng hóa!', 'bienbao', 'A2', 3),
(372, 'Biển nào báo hiệu chiều dài đoạn đường phải giữ cự ly tối thiểu giữa hai xe?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', '', 1, 0, '356.jpg', 'Biển phụ 1: Phạm vi tác dụng của biển. Biển phụ 2: Khoảng cách đến đối tượng báo hiệu. Vì vậy Biển 1 là đúng.', 'bienbao', 'A2', 3),
(373, 'Biển nào báo hiệu sắp đến chỗ giao nhau nguy hiểm?', 'Biển 1.', 'Biển 1 và 2.', 'Biển 2 và 3. ', 'Cả ba biển.', 4, 0, '374.jpg', 'Biển 1: Giao nhau đường sắt có rào chắn Biển 2: Giao nhau với đường ưu tiên Biển 3: Giao nhau có tín hiệu đèn Vì vậy đáp án đúng là cả ba biển.', 'bienbao', 'A2', 3),
(374, 'Biển nào báo hiệu “Giao nhau với đường không ưu tiên”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '392.jpg', 'Biển 1: Giao nhau đường cùng cấp Biển 2: Giao nhau với đường không ưu tiên Biển 3: Giao nhau với đường ưu tiên Vì vậy chọn Biển 2 là đúng.', 'bienbao', 'A2', 3),
(375, 'Biển nào sau đây là biển “Đường trơn”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '410.jpg', 'Biển 1: Chỗ ngoặt nguy hiểm có nguy cơ lật xe. Biển 2: Đường trơn. Biển 3: Qua bến phà. Vì vậy chọn Biển 2.', 'bienbao', 'A2', 3),
(376, 'Gặp biển báo này người lái xe phải xử lý thế nào?', 'Đi chậm, quan sát và dừng lại nếu gặp gia súc trên đường.', 'Bấm còi to để gia súc tránh đường và nhanh chóng di chuyển qua đoạn đường có gia súc.', '', '', 1, 0, '428.jpg', 'Gặp biển báo này thì phải đi chậm, quan sát hoặc có thể dừng lại hẳn.', 'bienbao', 'A2', 3),
(377, 'Khi đến chỗ giao nhau, gặp biển nào thì người lái xe không được cho xe đi thẳng, phải rẽ sang hướng khác?', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2 và 3.', 'Cả ba biển.', 1, 0, '446.jpg', 'Biển 1: Chỉ được rẽ trái, không được đi thẳng. Biển 2: Chỉ được rẽ trái hoặc phải. Biển 3: Đi thẳng hoặc rẽ phải. Vì vậy chọn biển 1 và biển 2.', 'bienbao', 'A2', 3),
(378, 'Biển báo dưới đây có ý nghĩa gì?', 'Chỉ dẫn khoảng cách đến làn đường cứu nạn (làn thoát xe khẩn cấp).', 'Báo hiệu đường cụt phía trước.', 'Báo hiệu nút giao gần nhất phía trước.', 'Báo hiệu trạm dừng nghỉ phía trước.', 1, 0, '464.jpg', 'Biển báo chỉ dẫn khoảng cách đến làn đường cứu nạn.!', 'bienbao', 'A2', 3),
(379, 'Các vạch dưới đây có tác dụng gì?', 'Phân chia hai chiều xe chạy ngược chiều nhau.', 'Phân chia các làn xe chạy cùng chiều nhau.', '', '', 2, 0, '482.jpg', 'Vạch màu trắng phân chia các làn xe chạy cùng chiều nhau.', 'sahinh', 'A2', 3),
(380, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe khách, xe tải, mô tô, xe con.', 'Xe con, xe khách, xe tải, mô tô. ', 'Mô tô, xe tải, xe khách, xe con.', 'Mô tô, xe tải, xe con, xe khách.', 3, 0, '504.jpg', 'Tại nơi đường giao nhau không có biển báo hiệu đi theo vòng xuyến, phải nhường đường cho xe đi đến từ bên phải. Vậy thứ tự xe đi: MÔ TÔ, XE TẢI, XE KHÁCH, XE CON.', 'sahinh', 'A2', 3),
(381, 'Xe con vượt xe tải như trường hợp này có đúng không?', 'Đúng.', 'Không đúng.', '', '', 1, 0, '530.jpg', 'Xe con đi thẳng, xe tải rẽ trái KHÔNG giao nhau nên được phép vượt trong trường hợp này.', 'sahinh', 'A2', 3),
(382, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe con và xe tải, xe của bạn. ', 'Xe của bạn, xe tải, xe con.', 'Xe của bạn và xe con, xe tải.', 'Xe của bạn, xe tải + xe con.', 3, 0, '551.jpg', 'Thứ tự xét xe trong sa hình: Xe ưu tiên – Đường ưu tiên – Phía bên phải trống – Rẽ phải – Đi thẳng – Rẽ Trái.Xe của bạn đang trên đường ưu tiên nên được đi trước. Xe con rẽ phải không giao nhau với xe của bạn nên được đi đồng thời cùng xe bạn. Xe tải rẽ trái từ đường không ưu tiên nên phải nhường đường đi sau cùng.', 'sahinh', 'A2', 3),
(383, 'Bạn xử lý như thế nào trong trường hợp này?', 'Nhường đường cho xe khách và đi trước xe đạp. ', 'Nhường đường cho xe đạp và đi trước xe khách. ', 'Nhường đường cho xe đạp và xe khách.', '', 3, 0, '574.jpg', 'Trước mặt có biển báo STOP là phải dừng lại và nhường đường.', 'sahinh', 'A2', 3),
(384, 'Trong các khái niệm dưới đây, “dải phân cách” được hiểu như thế nào là đúng?', 'Là bộ phận của đường để ngăn cách không cho các loại xe vào những nơi không được phép.', 'Là bộ phận của đường để phân tách phần đường xe chạy và hành lang an toàn giao thông.', 'Là bộ phận của đường để phân chia mặt đường thành hai chiều xe chạy riêng biệt hoặc để phân chia phần đường của xe cơ giới và xe thô sơ.', '', 3, 0, '', 'Dải phân cách là để phân chia mặt đường thành hai chiều xe chạy riêng biệt.', 'luat', 'A2', 4),
(385, 'Người điều khiển xe môtô, ô tô, máy kéo trên đường mà trong máu hoặc hơi thở có nồng độ cồn có bị nghiêm cấm không?', 'Bị nghiêm cấm.', 'Không bị nghiêm cấm.', 'Không bị nghiêm cấm, nếu nồng độ cồn trong máu ở mức nhẹ, có thể điều khiển phương tiện tham gia giao thông.', '', 1, 1, '', 'Uống rượu bia có nồng độ cồn đều bị nghiêm cấm.', 'luat', 'A2', 4),
(386, 'Người lái xe không được quay đầu xe trong các trường hợp nào dưới đây?', 'Ở phần đường dành cho người đi bộ qua đường, trên cầu, đầu cầu, đường cao tốc, đường hẹp, đường dốc, tại nơi đường bộ giao nhau cùng mức với đường sắt.', 'Ở phía trước hoặc phía sau của phần đường dành cho người đi bộ qua đường, trên đường quốc lộ, tại nơi đường bộ giao nhau không cùng mức với đường sắt.', 'Cả ý 1 và ý 2.', '', 1, 1, '', 'Câu cẩn thận Ý 1: Phần đường dành cho người đi bộ, đường cao tốc cấm quay đầu xe Ý 2: Quay đầu ở phía trước và phía sau, không giao với đường sắt thì được quay đầu. Vì vậy chọn Ý 1.', 'luat', 'A2', 4),
(387, 'Người có GPLX mô tô hạng A3 được phép điều khiển loại xe nào dưới đây?', 'Xe mô tô ba bánh.', 'Xe mô tô hai bánh có dung tích xi-lanh từ 175 cm3 trở lên.', 'Các loại máy kéo nhỏ có trọng tải đến 1.000 kg.', '', 1, 0, '', 'GPLX A3: điều khiển xe mô tô ba bánh.', 'luat', 'A2', 4),
(388, 'Khi muốn chuyển hướng, người lái xe phải thực hiện như thế nào để đảm bảo an toàn giao thông?', 'Quan sát gương, ra tín hiệu, quan sát an toàn và chuyển hướng.', 'Quan sát gương, giảm tốc độ, ra tín hiệu chuyển hướng, quan sát an toàn và chuyển hướng.', 'Quan sát gương, tăng tốc độ, ra tín hiệu và chuyển hướng.', '', 2, 0, '', 'Chuyển hướng thì phải giảm tốc, báo tín hiệu.', 'luat', 'A2', 4),
(389, 'Tại ngã ba hoặc ngã tư không có đảo an toàn, người lái xe phải nhường đường như thế nào là đúng trong các trường hợp dưới đây?', 'Nhường đường cho người đi bộ đang đi trên phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ưu tiên, đường chính từ bất kỳ hướng nào tới; nhường đường cho xe ưu tiên, xe đi từ bên phải đến.', 'Nhường đường cho người đi bộ đang đứng chờ đi qua phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ngược chiều, đường nhánh từ bất kỳ hướng nào tới; nhường đường cho xe đi từ bên trái đến.', 'Không phải nhường đường.', '', 1, 0, '', 'Nhường đường cho người đang đi bộ sang đường, nhường đường cho xe ưu tiên.', 'luat', 'A2', 4),
(390, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có 01 làn xe cơ giới (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 60 km/h?', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Ô tô buýt, ô tô đầu kéo kéo sơ mi rơ moóc, xe mô tô, ô tô chuyên dùng (trừ ô tô trộn vữa, ô tô trộn bê tông).', '', 3, 0, '', 'Đường hai chiều KHÔNG CÓ dải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 50 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 60 km/h: Xe MÔTÔ, ÔTÔ Buýt. 70 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 80 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải dưới 3,5 tấn.', 'luat', 'A2', 4),
(391, 'Người điều khiển phương tiện tham gia giao thông đường bộ phải giảm tốc độ để có thể dừng lại một cách an toàn trong các trường hợp nào dưới đây?', 'Khi có người đi bộ, xe lăn của người khuyết tật qua đường; đến gần bến xe buýt, điểm dừng đỗ xe có khách đang lên, xuống xe.', 'Khi điều khiển phương tiện đi qua khu vực trạm kiểm soát tải trọng xe, trạm cảnh sát giao thông, trạm giao dịch thanh toán đối với các phương tiện sử dụng đường bộ.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Giảm tốc độ khi có người đi bộ, xe lăn qua đường, điểm dừng đỗ xe khách. Giảm tốc độ tại trạm thu phí, trạm CSGT.', 'luat', 'A2', 4),
(392, 'Người lái xe có văn hóa khi tham gia giao thông phải đáp ứng các điều kiện nào dưới đây?', 'Có trách nhiệm với bản thân và với cộng đồng; tôn trọng, nhường nhịn người khác.', 'Tận tình giúp đỡ người tham gia giao thông gặp hoạn nạn; giúp đỡ người khuyết tật, trẻ em và người cao tuổi.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Câu hỏi dễ nhớ!', 'luat', 'A2', 4);
INSERT INTO `ques` (`id`, `ques`, `ans1`, `ans2`, `ans3`, `ans4`, `correct_ans`, `important`, `ques_image`, `detail`, `type`, `pack`, `exam`) VALUES
(393, 'Các biện pháp tiết kiệm nhiên liệu khi chạy xe?', 'Bảo dưỡng xe theo định kỳ và có kế hoạch lộ trình trước khi chạy xe.', 'Kiểm tra áp suất lốp theo quy định và chạy xe với tốc độ phù hợp với tình trạng mặt đường và mật độ giao thông trên đường.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Để tiết kiệm nhiên liệu: Bảo dưỡng xe định kỳ, kiểm tra áp xuất lốp, chạy đúng tốc độ.', 'luat', 'A2', 4),
(394, 'Trên xe ô tô có trang bị thiết bị như hình vẽ, dùng để làm gì?', 'Thay lốp xe.', 'Chữa cháy.', 'Phá cửa kính xe ô tô trong các trường hợp khẩn cấp.', 'Vặn ốc để tháo lắp bánh xe.', 3, 0, '303.jpg', 'Công cụ phá cửa kính xe ô tô trong các trường hợp khẩn cấp.', 'luat', 'A2', 4),
(395, 'Biển nào cấm các phương tiện giao thông đường bộ rẽ phải?', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2 và 3.', 'Cả ba biển.', 1, 0, '321.jpg', 'Biển 1: Cấm tất cả phương tiện giao thông rẽ phải. Biển 2: Cấm tất cả phương tiện giao thông rẽ phải và quay đầu. Biển 3: Cấm ÔTÔ rẽ phải và quay đầu. Vì vậy chọn Biển 1 và Biển 2.', 'bienbao', 'A2', 4),
(396, 'Biển báo này có ý nghĩa như thế nào?', 'Tốc độ tối đa cho phép về ban đêm cho các phương tiện là 70 km/h.', 'Tốc độ tối thiểu cho phép về ban đêm cho các phương tiện là 70 km/h.', '', '', 1, 0, '339.jpg', 'Hạn chế tốc độ tối đa xe chạy về ban đêm. !', 'bienbao', 'A2', 4),
(397, 'Biển nào báo hiệu khoảng cách thực tế từ nơi đặt biển đến nơi cần cự ly tối thiểu giữa hai xe?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', '', 2, 0, '357.jpg', 'Biển phụ 1: Phạm vi tác dụng của biển. Biển phụ 2: Khoảng cách đến đối tượng báo hiệu. Vì vậy Biển 2 là đúng.', 'bienbao', 'A2', 4),
(398, 'Biển nào báo hiệu “Giao nhau với đường sắt có rào chắn”?', 'Biển 1.', 'Biển 2 và 3.', 'Biển 3.', '', 1, 0, '375.jpg', 'Biển 1: Giao nhau với đường sắt có rào chắn. Biển 2: Giao nhau với đường ưu tiên. Biển 3: Giao nhau có tín hiệu đèn. Vì vậy chọn Biển 1.', 'bienbao', 'A2', 4),
(399, 'Biển nào báo hiệu “Đường hai chiều”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 1, 0, '393.jpg', 'Biển 1: Báo hiệu đường hai chiều Biển 2: Nhường đường cho xe cơ giới đi ngược chiều qua đường hẹp. Biển 3: Ưu tiên đi qua đường hẹp. Vì vậy chọn Biển 1.', 'bienbao', 'A2', 4),
(400, 'Biển nào sau đây là biển “Lề đường nguy hiểm”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '411.jpg', 'Biển 1: Kè, vực sâu nguy hiểm bên phải Biển 2: Lề đường nguy hiểm. Biển 3: Chỗ ngoặc nguy hiểm có nguy cơ lật xe. Vì vậy chọn Biển 2.', 'bienbao', 'A2', 4),
(401, 'Biển báo này có ý nghĩa gì?', 'Báo hiệu khu vực nguy hiểm thường xuyên có sét đánh.', 'Báo hiệu khu vực có đường dây điện cắt ngang phía trên tuyến đường.', '', '', 2, 0, '429.jpg', 'Biển báo đường cáp điện ở phía trên.!', 'bienbao', 'A2', 4),
(402, 'Biển nào cho phép quay đầu xe?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', '', 3, 0, '447.jpg', 'Biển 1: Chỗ quay xe, cho phép quay đầu xe Biển 2: Khu vực quay xe, cho phép quay đầu xe Vì vậy chọn cả 2 biển.', 'bienbao', 'A2', 4),
(403, 'Tại đoạn đường có biển “Làn đường dành riêng cho từng loại xe” dưới đây, các phương tiện có được phép chuyển sang làn khác để đi theo hành trình mong muốn khi đến gần nơi đường bộ giao nhau hay không?', 'Được phép chuyển sang làn khác.', 'Không được phép chuyển sang làn khác, chỉ được đi trong làn quy định theo biển.', '', '', 1, 0, '465.jpg', 'Được phép chuyển làn khi gần đến nơi giao nhau.', 'bienbao', 'A2', 4),
(404, 'Khi gặp vạch kẻ đường nào các xe được phép đè vạch?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Vạch 1 và vạch 3.', 4, 0, '483.jpg', 'Vạch liền thì cấm đè vạch. Vạch đứt thì được đè vạch.', 'sahinh', 'A2', 4),
(405, 'Trong trường hợp này xe nào đỗ vi phạm quy tắc giao thông?', 'Xe tải.', 'Xe con và mô tô. ', 'Cả ba xe.', 'Xe con và xe tải.', 1, 0, '505.jpg', 'Biển chính cấm dừng xe và đỗ xe, biển phụ hình xe tải Nên chỉ cấm xe tải dừng xe và đỗ xe, xe con và mô tô không vi phạm.', 'sahinh', 'A2', 4),
(406, 'Xe nào vượt đúng quy tắc giao thông?', 'Xe tải.', 'Cả hai xe.', 'Xe con.', '', 3, 0, '531.jpg', 'Xe tải vi phạm vì làn đó là vạch liền. Xe con được phép vì làn đó là vạch đứt.', 'sahinh', 'A2', 4),
(407, 'Theo tín hiệu đèn, xe nào phải dừng lại là đúng quy tắc giao thông?', 'Xe khách, mô tô.', 'Xe tải, mô tô.', 'Xe con, xe tải.', '', 3, 0, '553.jpg', 'Xe con, xe tải đang ở làn đường đèn đỏ nên phải dừng lại.', 'sahinh', 'A2', 4),
(408, 'Xe nào phải nhường đường trong trường hợp này?', 'Xe con.', 'Xe tải.', 'Xe của bạn.', '', 2, 0, '575.jpg', 'Biển báo phía trước xe của bạn là biển báo giao nhau đường không ưu tiên, được ưu tiên qua nói giao nhau. Vì vậy xe của bạn và xe con được quyền đi trước. Xe tải đi trên đường không ưu tiên nên phải nhường đường.', 'sahinh', 'A2', 4),
(759, '“Dải phân cách” trên đường bộ gồm những loại nào?', 'Dải phân cách gồm loại cố định và loại di động.', 'Dải phân cách gồm tường chống ồn, hộ lan cứng và hộ lan mềm.', 'Dải phân cách gồm giá long môn và biển báo hiệu đường bộ.', '', 1, 0, '', 'Dải phân cách: Có cả di động và cố định.', 'luat', 'A2', 5),
(760, 'Sử dụng rượu bia khi lái xe, nếu bị phát hiện thì bị xử lý như thế nào?', 'Chỉ bị nhắc nhở.', 'Bị xử phạt hành chính hoặc có thể bị xử lý hình sự tùy theo mức độ vi phạm.', 'Không bị xử lý hình sự.', '', 2, 1, '', 'Có thể bị xử lý chánh và cả hình sự tùy theo mức độ vi phạm.', 'luat', 'A2', 5),
(761, 'Bạn đang lái xe trong khu dân cư, có đông xe qua lại, nếu muốn quay đầu bạn cần làm gì để tránh ùn tắc và đảm bảo an toàn giao thông?', 'Đi tiếp đến điểm giao cắt gần nhất hoặc nơi có biển báo cho phép quay đầu xe.', 'Bấm đèn khẩn cấp và quay đầu xe từ từ bảo đảm an toàn.', 'Bấm còi liên tục khi quay đầu để cảnh báo các xe khác.', 'Nhờ một người ra hiệu giao thông trên đường chậm lại trước khi quay đầu.', 1, 0, '', 'Khu vực dân cư có đông xe qua lại muốn quay đầu đi tiếp đến điểm cắt gần nhất, nơi có biển báo để quay đầu xe.', 'luat', 'A2', 5),
(762, 'Biển báo hiệu có dạng hình tròn, viền đỏ, nền trắng, trên nền có hình vẽ hoặc chữ số, chữ viết màu đen là loại biển gì dưới đây?', 'Biển báo nguy hiểm.', 'Biển báo cấm.', 'Biển báo hiệu lệnh.', 'Biển báo chỉ dẫn.', 2, 0, '72.jpg', 'Cấm hình tròn viền đỏ.', 'luat', 'A2', 5),
(763, 'Khi lùi xe người lái xe phải làm gì để bảo đảm an toàn?', 'Quan sát phía trước và cho lùi xe ở tốc độ chậm.', 'Lợi dụng nơi đường giao nhau đủ chiều rộng để lùi.', 'Phải quan sát phía sau, có tín hiệu cần thiết và chỉ khi nào thấy không nguy hiểm mới được lùi.', '', 3, 0, '', 'Lùi xe thì phải quan sát, không nguy hiểm mới được lùi.', 'luat', 'A2', 5),
