(1022, 'Trong các biển báo dưới đây biển nào báo hiệu \"Hết đường cao tốc\"?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '367.jpg', 'Biển 2 là hết đường cao tốc!', 'bienbao', 'A2', 14),
(1023, 'Biển nào báo hiệu, chỉ dẫn xe đi trên đường này được quyền ưu tiên qua nơi giao nhau?', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2 và 3.', 'Cả ba biển.', 2, 0, '385.jpg', 'Biển 1: Giao nhau với đường không ưu tiên. Biển 2: Giao nhau với đường ưu tiên. Biển 3: Ưu tiên qua nơi giao nhau. Nên biển 1 và 3 là đúng.', 'bienbao', 'A2', 14),
(1024, 'Biển nào chỉ dẫn cho người tham gia giao thông biết vị trí và khoảng cách có làn đường cứu nạn hay làn thoát xe khẩn cấp?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', '', 2, 0, '403.jpg', 'Biển 1 là nó cấm đi thẳng, có hướng khác đi vòng, gọi là đường tránh. Biển 2 là làn đường cứu nạn hay làn thoát xe.', 'bienbao', 'A2', 14),
(1025, 'Biển báo này có ý nghĩa gì?', 'Chỗ ngoặt nguy hiểm có nguy cơ lật xe bên phải khi đường cong vòng sang trái.', 'Chỗ ngoặt nguy hiểm có nguy cơ lật xe bên trái khi đường cong vòng bên phải.', '', '', 1, 0, '421.jpg', 'Biển báo có hình Xe sắp bị lật qua bên phải!', 'bienbao', 'A2', 14),
(1026, 'Trong các biển dưới đây biển nào là biển “Hết hạn chế tốc độ tối đa”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Cả ba biển.', 1, 0, '439.jpg', 'Biển 1: Hết Hạn Chế Tốc Độ Tối Đa. Biển 2: Hết mọi lệnh cấm. Biển 3: Hết hạn chế tốc độ tối thiểu. Nên chọn biển 1.', 'bienbao', 'A2', 14),
(1027, 'Gặp biển báo này, người lái xe phải đỗ xe như thế nào?', 'Đỗ xe hoàn toàn trên hè phố.', 'Đỗ xe hoàn toàn dưới lòng đường.', 'Đỗ từ ½ thân xe trở lên trên hè phố.', '', 3, 0, '457.jpg', 'Biển báo có hình nữa bánh xe dưới đường nữa bánh xe kia trên vĩa hè nên chọn ½.', 'bienbao', 'A2', 14),
(1028, 'Trên đường cao tốc, gặp biển nào thì người lái xe đi được cả hai hướng (bên trái hoặc bên phải) để tránh chướng ngại vật?', 'Biển 1.', 'Biển 2.', 'Biển 3.', '', 2, 0, '475.jpg', 'Biển 1 đặt bên trái, biển 2 nằm giữa và biển 3 đặt bên phải. Câu hỏi là cả hai hướng nên chọn chính giữa Biển 2.', 'bienbao', 'A2', 14),
(1029, 'Xe nào phải nhường đường là đúng quy tắc giao thông?', 'Xe con.', 'Xe tải.', '', '', 1, 0, '497.jpg', 'Xe tải: bên phải trống nên đi trước. Nên xe con nhường đường là đúng quy tắc.', 'sahinh', 'A2', 14),
(1030, 'Xe nào được quyền đi trước trong trường hợp này?', 'Xe con. ', 'Xe mô tô ', '', '', 2, 0, '517.jpg', 'Đường cùng cấp: rẽ phải, đi thẳng, rẽ trái. Xe mô tô rẽ phải nên được đi trước. Xe con rẽ trái thì đi cuối.', 'sahinh', 'A2', 14),
(1031, 'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?', 'Xe con (E), mô tô (C).', 'Xe tải (A), mô tô (D).', 'Xe tải (A), mô tô (D).', 'Xe khách (B), mô tô (D).', 1, 0, '543.jpg', 'Xe mô tô C chạy vào làn đường xe ô tô vi phạm. Xe con E chạy vào làn đường riêng xe máy vi phạm.', 'sahinh', 'A2', 14),
(1032, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con. ', 'Xe con, xe của bạn, mô tô. ', 'Mô tô, xe con, xe của bạn.', '', 3, 0, '567.jpg', 'Sa hình có biển báo giao nhau với đường ưu tiên và biển phụ ưu tiên rẽ trái. Nên mô tô đi trước. tiếp đến xe con và cuối cùng xe của bạn.', 'sahinh', 'A2', 14),
(1033, 'Xe nào dừng đúng theo quy tắc giao thông?', 'Xe con.', 'Xe mô tô.', 'Cả 2 xe đều đúng.', '', 1, 0, '592.jpg', 'Giao nhau với đường sắt không có rào chắn. Phương tiện đứng cách đường ray tối thiểu 5 mét. Nên xe con dừng đúng.', 'sahinh', 'A2', 14),
(1034, 'Khái niệm “đỗ xe” được hiểu như thế nào là đúng?', 'Là trạng thái đứng yên của phương tiện giao thông có giới hạn trong một khoảng thời gian cần thiết đủ để cho người lên, xuống phương tiện đó, xếp dỡ hàng hóa hoặc thực hiện công việc khác.', 'Là trạng thái đứng yên của phương tiện giao thông không giới hạn thời gian.', '', '', 2, 0, '', 'Đỗ xe thì không giới hạn thời gian.', 'luat', 'A2', 15),
(1035, 'Hành vi lắp đặt, sử dụng còi, đèn không đúng thiết kế của nhà sản xuất đối với từng loại xe cơ giới có được phép hay không?', 'Được phép.', 'Không được phép.', 'Được phép tùy từng trường hợp.', '', 2, 1, '', 'KHÔNG ĐƯỢC PHÉP lắp đặt, sử dụng còi không đúng thiết kế.', 'luat', 'A2', 15),
(1036, 'Khi đang lên dốc người ngồi trên xe mô tô có được kéo theo người đang điều khiển xe đạp hay không?', 'Chỉ được phép nếu cả hai đội mũ bảo hiểm. ', 'Không được phép.', 'Chỉ được thực hiện trên đường thật vắng.', 'Chỉ được phép khi người đi xe đạp đã quá mệt.', 2, 1, '', 'Xe môtô KHÔNG ĐƯỢC PHÉP kéo xe khác.', 'luat', 'A2', 15),
(1037, 'Tại nơi có biển báo hiệu cố định lại có báo hiệu tạm thời thì người tham gia giao thông phải chấp hành hiệu lệnh của báo hiệu nào?', 'Biển báo hiệu cố định. ', 'Báo hiệu tạm thời.', '', '', 2, 0, '', 'Chấp hành theo hiệu lệnh biển báo tạm thời.', 'luat', 'A2', 15),
(1038, 'Việc nối giữa xe kéo với xe được kéo trong trường hợp hệ thống hãm của xe được kéo không còn hiệu lực thì phải dùng cách nào?', 'Dùng dây cáp có độ dài 10 mét. ', 'Dùng dây cáp có độ dài 5 mét. ', 'Dùng thanh nối cứng.', '', 3, 0, '', 'Dùng thanh nối cứng để đồng nhất tốc độ với xe kéo.', 'luat', 'A2', 15),
(1039, 'Trên đường bộ (trừ đường cao tốc) trong khu vực đông dân cư, đường đôi hoặc đường một chiều có từ hai làn xe cơ giới trở lên, loại xe nào dưới đây được tham gia giao thông với tốc độ tối đa cho phép là 60 km/h?', 'Ô tô con, ô tô tải, ô tô chở người trên 30 chỗ. ', 'Xe gắn máy, xe máy chuyên dùng.', 'Cả ý 1 và ý 2.', '', 1, 0, '', 'Xe gắn máy, xe máy chuyên dung chỉ chạy tối đa 40km/h dù trong khu dân cư hay ngoài khu dân cư.', 'luat', 'A2', 15),
(1040, 'Tác dụng của mũ bảo hiểm đối với người ngồi trên xe mô tô hai bánh trong trường hợp xảy ra tai nạn giao thông là gì?', 'Để làm đẹp.', 'Để tránh mưa nắng.', 'Để giảm thiểu chấn thương vùng đầu.', 'Để các loại phương tiện khác dễ quan sát.', 3, 0, '', 'Mũ bảo hiểm có tác dụng bảo vệ phần đầu của người lái xe.', 'luat', 'A2', 15),
(1041, 'Người lái xe vận tải hàng hóa cần thực hiện những nhiệm vụ gì ghi ở dưới đây?', 'Thực hiện nghiêm chỉnh những nội dung hợp đồng giữa chủ phương tiện với chủ hàng trong việc vận chuyển và bảo quản hàng hóa trong quá trình vận chuyển; không chở hàng cấm, không xếp hàng quá trọng tải của xe, quá trọng tải cho phép của cầu, đường; khi vận chuyển hàng quá khổ, quá tải, hàng nguy hiểm, hàng siêu trường, siêu trọng phải có giấy phép.', 'Thực hiện nghiêm chỉnh những nội dung hợp đồng giữa chủ hàng với khách hàng trong việc vận chuyển và bảo quản hàng hóa trong quá trình vận chuyển; trong trường hợp cần thiết  có thể xếp hàng quá trọng tải của xe, quá trọng tải cho phép của cầu theo yêu cầu của chủ hàng; khi vận chuyển hàng quá khổ, quá tải, hàng nguy hiểm, hàng siêu trường, siêu trọng phải được chủ hàng cho phép.', '', '', 1, 0, '', 'Không chở hàng cấm và chở hàng phải có giấy phép.', 'luat', 'A2', 15);
INSERT INTO `ques` (`id`, `ques`, `ans1`, `ans2`, `ans3`, `ans4`, `correct_ans`, `important`, `ques_image`, `detail`, `type`, `pack`, `exam`) VALUES
(1042, 'Trong đoạn đường hai chiều tại khu đông dân cư đang ùn tắc, người điều khiển xe mô tô hai bánh có văn hóa giao thông sẽ lựa chọn xử lý tình huống nào dưới đây?', 'Cho xe lấn sang làn ngược chiều để nhanh chóng thoát khỏi nơi ùn tắc.', 'Điều khiển xe lên vỉa hè để nhanh chóng thoát khỏi nơi ùn tắc.', 'Kiên nhẫn tuân thủ hướng dẫn của người điều khiển giao thông hoặc tín hiệu giao thông, di chuyển trên đúng phần đường bên phải theo chiều đi, nhường đường cho các phương tiện đi ngược chiều để nút tắc nhanh chóng được giải tỏa.', '', 3, 0, '', 'Đang kẹt xe thì phải kiên nhẫn.', 'luat', 'A2', 15),
(1043, 'Để đảm bảo an toàn khi tham gia giao thông, người lái xe lái xe mô tô hai bánh cần điều khiển tay ga như thế nào trong các trường hợp dưới đây?', 'Tăng ga thật nhanh, giảm ga từ từ.', 'Tăng ga thật nhanh, giảm ga thật nhanh. ', 'Tăng ga từ từ, giảm ga thật nhanh.', 'Tăng ga từ từ, giảm ga từ ', 3, 0, '', 'Tăng ga từ từ, giảm ga thật nhanh mới điều tiết được tốc độ chạy của xe và an toàn hơn.', 'luat', 'A2', 15),
(1044, 'Biển nào cấm ô tô tải vượt?', 'Biển 1.', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2 và 3.', 4, 0, '314.jpg', 'Biển 1: Hết cấm ô tô con vượt nên xe tải được vượt Biển 2: Cấm xe con và cũng cấm luôn xe tải vượt. Biển 3: Cấm xe tải vượt. Biển 2 và 3 là đáp án đúng.', 'bienbao', 'A2', 15),
(1045, 'Gặp biển nào người lái xe không được đỗ xe vào ngày lẻ?', 'Biển 1 và 2.', 'Biển 2.', 'Biển 2 và 3.', 'Biển 3.', 1, 0, '332.jpg', 'Biển 1: Cấm đậu xe, ngày nào cũng cấm. Biển 2: Cấm đậu xe ngày lẽ. Biển 3: Cấm đậu xe ngày chẵn. Vì vậy chọn biển 1 và biển 2.', 'bienbao', 'A2', 15),
(1046, 'Biển này có ý nghĩa gì?', 'Cấm xe cơ giới (trừ xe ưu tiên theo luật định) đi thẳng.', 'Cấm xe ô tô và mô tô (trừ xe ưu tiên theo luật định) đi về bên trái và bên phải.', 'Hướng trái và phải không cấm xe cơ giới.', '', 2, 0, '350.jpg', 'Biển phụ phía dưới là cấm đi về phía bên trái hoặc bên phải.', 'bienbao', 'A2', 15),
(1047, 'Số 50 ghi trên biển báo dưới đây có ý nghĩa gì?', 'Tốc độ tối đa các xe cơ giới được phép chạy.', 'Tốc độ tối thiểu các xe cơ giới được phép chạy.', '', '', 1, 0, '368.jpg', 'Không được chạy quá 50km/h.', 'bienbao', 'A2', 15),
(1048, 'Biển nào báo hiệu “Giao nhau với đường không ưu tiên”?', 'Biển 1.', 'Biển 2', 'Biển 3.', 'Biển 2 và 3.', 1, 0, '386.jpg', 'Biển 1: Giao nhau với đường không ưu tiên. Biển 2: Giao nhau với đường ưu tiên. Biển 3: Đường ưu tiên. Vì vậy chọn biển 1.', 'bienbao', 'A2', 15),
(1049, 'Biển nào báo hiệu “Đường hầm”?', 'Cả ba biển.', 'Biển 2.', 'Biển 2 và 3.', '', 2, 0, '404.jpg', 'Biển số 1: Báo hiệu qua cửa chui. Biển số 2: Báo hiệu đường hầm. Biển số 3: Báo hiệu qua cầu. Nên chọn biển 2.', 'bienbao', 'A2', 15),
(1050, 'Biển báo này có ý nghĩa gì?', 'Báo trước đoạn đường có gió ngang. ', 'Báo trước đoạn đường trơn trượt', 'Báo trước sắp đến bến phà.', '', 3, 0, '422.jpg', 'Biển cảnh cáo sắp đến bến phà.', 'bienbao', 'A2', 15),
(1051, 'Hiệu lực của biển \"Tốc độ tối đa cho phép\"hết tác dụng khi gặp biển nào dưới đây?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 1 và 2.', 4, 0, '440.jpg', 'Biến 1: Hết hạn chế tốc độ tối đa. Biển 2: Hết mọi lệnh cấm. Biển 3: Hết hạn chế tốc độ tối thiểu. Nên biển 1 và biển 2 đều hết hạn chế tốc độ tối đa cho phép.', 'bienbao', 'A2', 15),
(1052, 'Gặp biển báo này, người tham gia giao thông phải xử lý như thế nào?', 'Dừng xe tại khu vực có trạm Cảnh sát giao thông.', 'Tiếp tục lưu thông với tốc độ bình thường.', 'Phải giảm tốc độ đến mức an toàn và không được vượt khi đi qua khu vực này.', '', 3, 0, '458.jpg', 'Gặp biển báo này thì phải giảm tốc độ và không được vượt khi qua khu vực này.', 'bienbao', 'A2', 15),
(1053, 'Trên đường cao tốc, gặp biển nào người lái xe phải chú ý đổi hướng đi khi sắp vào đường cong nguy hiểm?', 'Biển 1 và 2.', 'Biển 1 và 3.', 'Biển 2.', 'Biển 3.', 2, 0, '476.jpg', 'Biển 2: Cảnh báo phía trước có chướng ngại vật. Biển 1 và Biển 3: Đường cong nguy hiểm.', 'bienbao', 'A2', 15),
(1054, 'Xe nào được quyền đi trước trong trường hợp này?', 'Xe công an.', 'Xe chữa cháy.', '', '', 2, 0, '498.jpg', 'Thứ tự xe ưu tiên: 1. Xe cứ hỏa 2. Xe Quân sự 3. Xe Công an 4. Xe cứu thương Xe cứu hỏa ưu tiên hơn xe công an vì mức độ thiệt hại.', 'sahinh', 'A2', 15),
(1055, 'Xe nào được quyền đi trước trong trường hợp này', 'Mô tô.', 'Xe con.', '', '', 1, 0, '525.jpg', 'Biển báo chính đường ưu tiên và biển phụ ưu tiên rẽ trái. Nên xe mô tô được quyền rẽ trái trước.', 'sahinh', 'A2', 15),
(1056, 'Để điều khiển cho xe đi thẳng, người lái xe phải làm gì là đúng quy tắc giao thông?', 'Nhường xe con rẽ trái trước.', 'Đi thẳng không nhường.', '', '', 1, 0, '544.jpg', 'Biển báo này là giao nhau cùng cấp, xe con rẽ trái lẽ ra là đi cuối nhưng xe đó đã vào ngã tư nên nó được đi trước.', 'sahinh', 'A2', 15),
(1057, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con. ', 'Xe con, xe của bạn, mô tô. ', 'Mô tô, xe con, xe của bạn.', '', 2, 0, '568.jpg', 'Đường cùng cấp thứ tự xe đi: rẽ phải > đi thẳng > rẽ trái. Thứ tự đi đúng là: 1. Xe con: rẽ phải. 2. Xe của bạn: đi thẳng. 3. xe mô tô rẽ trái.', 'sahinh', 'A2', 15),
(1058, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe tải, xe đạp, xe của bạn.', 'Xe của bạn, xe đạp, xe tải. ', 'Xe của bạn, xe tải, xe đạp.', '', 3, 0, '593.jpg', 'Đường cùng cấp: bên phải xe nào trống xe đó được đi. 1. Xe của bạn: bên phải trống đi trước 2. Xe tải: rẽ trái bên phải trống khi xe của bạn đã đi. 3. Xe đạp: rẽ trái cuối cùng.', 'sahinh', 'A2', 15),
(1059, 'Khái niệm “đường cao tốc” được hiểu như thế nào là đúng?', 'Đường dành riêng cho xe ôtô và một số loại xe chuyên dùng được phép đi vào theo quy định của Luật Giao thông đường bộ.', 'Có dải phân cách phân chia đường cho xe chạy hai chiều riêng biệt mà dải phân cách này xe không đi được lên trên; không giao nhau cùng mức với một hoặc một số đường khác.', 'Được bố trí đầy đủ trang thiết bị phục vụ, bảo đảm giao thông liên tục, an toàn, rút ngắn thời gian hành trình và chỉ cho xe ra, vào ở những điểm nhất định.', 'Tất cả các ý trên.', 4, 0, '', 'Mẹo: Gặp đán án có tất cả các ý nêu trên thì chọn ngay là chắc chắn đúng.', 'luat', 'A2', 16),
(1060, 'Trong trường hợp đặc biệt, để được lắp đặt, sử dụng còi, đèn không đúng với thiết kế của nhà sản xuất đối với từng loại xe cơ giới bạn phải đảm bảo yêu cầu nào dưới đây?', 'Phải đảm bảo phụ tùng do đúng nhà sản xuất đó cung cấp. ', 'Phải được chấp thuận của cơ quan có thẩm quyền.', 'Phải là xe đăng ký và hoạt động tại các khu vực có địa hình phức tạp.', '', 2, 0, '', 'Muốn lắp đặt còi, đèn thêm thì phải có giấy phép cho cơ quan có thẩm quyền cho phép.', 'luat', 'A2', 16),
(1061, 'Hành vi sử dụng xe mô tô để kéo, đẩy xe mô tô khác bị hết xăng đến trạm mua xăng có được phép hay không?', 'Chỉ được kéo nếu đã nhìn thấy trạm xăng.', 'Chỉ được thực hiện trên đường vắng phương tiện cùng tham gia giao thông.', 'Không được phép.', '', 3, 1, '', 'Mẹo: Gặp đán án có chữ không được phép thì chọn ngay là chắc chắn đúng!', 'luat', 'A2', 16),
(1062, 'Trên đường có nhiều làn đường cho xe đi cùng chiều được phân biệt bằng vạch kẻ phân làn đường, người điều khiển phương tiện phải cho xe đi như thế nào?', 'Cho xe đi trên bất kỳ làn đường nào hoặc giữa 02 làn đường nếu không có xe phía trước; khi cần thiết phải chuyển làn đường, người lái xe phải quan sát xe phía trước để bảo đảm an toàn.', 'Phải cho xe đi trong một làn đường và chỉ được chuyển làn đường ở những nơi cho phép; khi chuyển làn phải có tín hiệu báo trước và phải bảo đảm an toàn.', 'Phải cho xe đi trong một làn đường, khi cần thiết phải chuyển làn đường, người lái xe phải quan sát xe phía trước để bảo đảm an toàn.', '', 2, 0, '', 'Chỉ chuyển làn ở nơi cho phép và phải đảm bảo an toàn.', 'luat', 'A2', 16),
(1063, 'Người ngồi trên xe mô tô 2 bánh, xe gắn máy phải đội mũ bảo hiểm có cài quai đúng quy cách khi nào?', 'Khi tham gia giao thông đường bộ.', 'Chỉ khi đi trên đường chuyên dùng; đường cao tốc.', 'Khi tham gia giao thông trên đường tỉnh lộ hoặc quốc lộ.', '', 1, 1, '', 'Đội nón bảo hiểm khi tham gia giao thông.', 'luat', 'A2', 16),
(1064, 'Trên đường bộ (trừ đường cao tốc) ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa, loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 90 km/h?', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3,5 tấn.', 'Ô tô buýt, ô tô đầu kéo kéo sơ mi rơ moóc, ô tô chuyên dùng, xe mô tô.', '', 2, 0, '', 'Đường đôi có giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 60 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 70 km/h: Xe MÔTÔ, ÔTÔ Buýt. 80 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 90 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải.', 'luat', 'A2', 16),
(1065, 'Khi điều khiển phương tiện tham giao giao thông, những hành vi nào dưới đây bị nghiêm cấm?', 'Thay đổi tốc độ của xe trên đường bộ. ', 'Thay đổi tay số của xe trên đường bộ. ', 'Lạng lách, đánh võng trên đường bộ.', '', 3, 1, '', 'Lạng lách, đánh võng là bị nghiêm cấm.', 'luat', 'A2', 16),
(1066, 'Người lái xe kinh doanh vận tải cần thực hiện những công việc gì ghi ở dưới đây để thường xuyên rèn luyện nâng cao đạo đức nghề nghiệp?', 'Phải yêu quý xe, quản lý và sử dụng xe tốt; bảo dưỡng xe đúng định kỳ; thực hành tiết kiệm vật tư, nhiên liệu; luôn tu dưỡng bản thân, có lối sống lành mạnh, tác phong làm việc công nghiệp.', 'Nắm vững các quy định của pháp luật, tự giác chấp hành pháp luật, lái xe an toàn; coi hành khách như người thân, là đối tác tin cậy; có ý thức tổ chức kỷ luật và xây dựng doanh nghiệp vững mạnh; có tinh thần hợp tác, tương trợ, giúp đỡ đồng nghiệp.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Đọc cả ý 1và ý 2 để phân tích, nếu thấy cả 2 đều đúng thì chọn cả 2 ý!', 'luat', 'A2', 16),
(1067, 'Trên đường đang xảy ra ùn tắc những hành vi nào sau đây là thiếu văn hóa khi tham gia giao thông?', 'Bấm còi liên tục thúc giục các phương tiện phía trước nhường đường.', 'Đi lên vỉa hè, tận dùng mọi khoảng trống để nhanh chóng thoát khỏi nơi ùn tắc.', 'Lấn sang trái đường cố gắng vượt lên xe khác. ', 'Tất cả các ý nêu trên.', 4, 0, '', 'Gặp đáp án có tất cả các ý nêu trên thì chọn là chắc chắn đúng.', 'luat', 'A2', 16),
(1068, 'Kỹ thuật cơ bản để giữ thăng bằng khi điều khiển xe mô tô đi trên đường gồ ghề như thế nào trong các trường hợp dưới đây?', 'Đứng thẳng trên giá gác chân lái sau đó hơi gập đầu gối và khuỷu tay, đi chậm để không nẩy quá mạnh.', 'Ngồi lùi lại phía sau, tăng ga vượt nhanh qua đoạn đường xóc. ', 'Ngồi lệch sang bên trái hoặc bên phải để lấy thăng bằng qua đoạn đường gồ ghề.', '', 1, 0, '', 'Chạy qua gờ giảm tốc phải hạ khuỷu tay xuống thấp để khi nẩy lên thì không mất lái!', 'luat', 'A2', 16),
(1069, 'Biển nào cấm xe tải vượt?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', '', 3, 0, '315.jpg', 'Biển 1: Cấm ô tô vượt và cả xe tải vượt. Biển 2: Cấm xe tải vượt. Nên cả 2 biển.', 'bienbao', 'A2', 16),
(1070, 'Khi gặp biển nào xe ưu tiên theo luật định vẫn phải dừng lại?', 'Biển 1.', 'Biển 2.', 'Cả ba biển.', '', 2, 0, '333.jpg', 'Biển có chữ STOP thì dù là xe ưu tiên cũng phải dừng lại.', 'bienbao', 'A2', 16),
(1071, 'Biển phụ đặt dưới biển cấm bóp còi có ý nghĩa gì?', 'Báo khoảng cách đến nơi cấm bóp còi.', 'Chiều dài đoạn đường cấm bóp còi từ nơi đặt biển.', 'Báo cấm dùng còi có độ vang xa 500 m.', '', 2, 0, '351.jpg', 'Biển chính là cấm còi. Biển phụ phạm vi tác dụng của biển xác định chiều dài áp dụng biển chính từ nơi đặt biển.', 'bienbao', 'A2', 16),
(1072, 'Trong các biển nào dưới đây biển nào chỉ dẫn bắt đầu đường cao tốc?', 'Biển 1.', 'Biển 2', 'Cả Biển 1 và Biển 2.', '', 2, 0, '369.jpg', 'Biến 1: là báo hiệu sắp tới đường cao tốc. Biển 2: là bắt đầu đường cao tốc.', 'bienbao', 'A2', 16),
