(1080, 'Ô tô con đi theo chiều mũi tên có vi phạm quy tắc giao thông không?', 'Không vi phạm. ', 'Vi phạm.', '', '', 2, 0, '526.jpg', 'Biển báo bắt buộc chỉ đi thẳng, không rẽ hướng khác.', 'sahinh', 'A2', 16),
(1081, 'Người lái xe điều khiển xe chạy theo hướng nào là đúng quy tắc giao thông?', 'Chỉ hướng 2.', 'Hướng 1 và 2.', 'Tất cả các hướng trừ hướng 3. ', 'Tất cả các hướng trừ hướng 4. ', 2, 0, '545.jpg', 'Biển cấm rẽ phải nên hướng 4 không được đi Biển cấm đi ngược chiều nên hướng 3 không được đi Chỉ được đi hướng 1 và 2.', 'sahinh', 'A2', 16),
(1082, 'Xe nào phải dừng lại trong trường hợp này?', 'Xe con.', 'Xe của bạn.', 'Cả hai xe.', '', 1, 0, '569.jpg', 'Xe con dừng lại vì phía trước tín hiệu đèn đỏ. Xe của bạn rẽ phải trước đèn đỏ nên được đi.', 'sahinh', 'A2', 16),
(1083, 'Để điều khiển xe rẽ trái, bạn chọn hướng đi nào dưới đây?', 'Hướng 1.', 'Hướng 2.', 'Cả 02 hướng đều được.', '', 1, 0, '594.jpg', 'Vạch kẻ đường nét đứt màu vàng phân chia 2 chiều xe chạy. Muốn rẽ trái thì đi theo hướng số 1, hướng số 2 là hướng xe ngược chiều chạy nên không được phép.', 'sahinh', 'A2', 16),
(1084, 'Hành vi nào dưới đây bị nghiêm cấm?', 'Đỗ xe trên đường phố', 'Sử dụng xe đạp đi trên các tuyến quốc lộ có tốc độ cao.', 'Làm hỏng (cố ý) cọc tiêu, gương cầu, dải phân cách.', 'Sử dụng còi và quay đầu xe trong khu dân cư.', 3, 1, '', 'Làm hư hỏng phá hoại tài sản đất nước là bị nghiêm cấm!', 'luat', 'A2', 17),
(1085, 'Việc sản xuất, mua bán, sử dụng biển số xe cơ giới, xe máy chuyên dùng được quy định như thế nào trong Luật Giao thông đường bộ?', 'Được phép sản xuất, sử dụng khi bị mất biển số. ', 'Được phép mua bán, sử dụng khi bị mất biển số.', 'Nghiêm cấm sản xuất, mua bán, sử dụng trái phép.', '', 3, 1, '', 'Nghiêm cấm là cấm hết. CẤM sản xuất, mua bán hay sử dụng biển số giả.', 'luat', 'A2', 17),
(1086, 'Hành vi vận chuyển đồ vật cồng kềnh bằng xe mô tô, xe gắn máy khi tham gia giao thông có được phép hay không?', 'Không được vận chuyển.', 'Chỉ được vận chuyển khi đã chằng buộc cẩn thận.', 'Chỉ được vận chuyển vật cồng kềnh trên xe máy nếu khoảng cách về nhà ngắn hơn 2 km.g.', '', 1, 1, '', 'Chở đồ cồng kềnh trên xe gắn máy thì rất nguy hiểm cho xe khác.', 'luat', 'A2', 17),
(1087, 'Trên đường một chiều có vạch kẻ phân làn đường, xe thô sơ và xe cơ giới phải đi như thế nào là đúng quy tắc giao thông?', 'Xe thô sơ phải đi trên làn đường bên trái trong cùng, xe cơ giới, xe máy chuyên dùng đi trên làn đường bên phải.', 'Xe thô sơ phải đi trên làn đường bên phải trong cùng; xe cơ giới, xe máy chuyên dùng đi trên làn đường bên trái.', 'Xe thô sơ đi trên làn đường phù hợp không gây cản trở giao thông, xe cơ giới, xe máy chuyên dùng đi trên làn đường bên phải.', '', 2, 0, '', 'Xe thô sơ chạy chậm nên phải đi làn đường bên phải trong cùng.', 'luat', 'A2', 17),
(1088, 'Người điểu khiển xe mô tô hai bánh, xe gắn máy được phép chở tối đa 2 người trong những trường hợp nào?', 'Chở người bệnh đi cấp cứu; trẻ em dưới 14 tuổi. ', 'Áp giải người có hành vi vi phạm pháp luật.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Có đáp án cả ý 1 và 2 thì phải đọc cẩn thận để phân tích.', 'luat', 'A2', 17),
(1089, 'Trên đường bộ ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 80 km/h?', 'Ô tô buýt; ô tô đầu kéo kéo sơ mi rơ moóc; ô tô chuyên dùng; xe mô tô.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn (trừ ô tô xi téc).', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3,5 tấn.', 2, 0, '', 'Đường đôi có giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 60 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 70 km/h: Xe MÔTÔ, ÔTÔ Buýt. 80 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 90 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải', 'luat', 'A2', 17),
(1090, 'Người lái xe phải nhanh chóng giảm tốc độ, tránh hoặc dừng lại sát lề đường bên phải để nhường đường cho các xe nào nêu dưới đây?', 'Xe chữa cháy, xe quân sự, xe công an, xe cứu thương, xe hộ đê sau khi thực hiện nhiệm vụ khẩn cấp, không có tín hiệu còi, cờ, đèn theo quy định của pháp luật.', 'Xe chữa cháy, xe quân sự, xe công an, xe cứu thương, xe hộ đê đi làm nhiệm vụ khẩn cấp có tín hiệu còi, cờ, đèn theo quy định của pháp luật.', 'Xe ô tô, xe máy, đoàn xe đang diễu hành có tổ chức có báo tín hiệu xin vượt bằng còi và đèn.', '', 2, 0, '', 'Xe ưu tiên đang đi làm nhiệm vụ thì mình phải giảm tốc độ và nhường đường.', 'luat', 'A2', 17),
(1091, 'Người lái xe và nhân viên phục vụ trên xe ô tô vận tải hành khách phải có những trách nhiệm gì theo quy định dưới đây?', 'Kiểm tra các điều kiện bảo đảm an toàn của xe sau khi khởi hành; có trách nhiệm lái xe thật nhanh khi chậm giờ của khách.', 'Kiểm tra các điều kiện bảo đảm an toàn của xe trước khi khởi hành; có thái độ văn minh, lịch sự, hướng dẫn hành khách ngồi đúng nơi quy định; kiểm tra việc sắp xếp, chằng buộc hành lý, bảo đảm an toàn.', 'Có biện pháp bảo vệ tính mạng, sức khỏe, tài sản của hành khách đi xe, giữ gìn trật tự, vệ sinh trong xe; đóng cửa lên xuống của xe trước và trong khi xe chạy.', 'Cả ý 2 và ý 3.', 4, 0, '', 'Mẹo: Gặp đáp án có cả ý 2 và ý 3 thì chọn ngay là đáp án là chắc chắn đúng!', 'luat', 'A2', 17),
(1092, 'Khi điều khiển xe mô tô tay ga xuống đường dốc dài, độ dốc cao, người lái xe cần thực hiện các thao tác nào dưới đây để đảm bảo an toàn?', 'Giữ tay ga ở mức độ phù hợp, sử dụng phanh trước và phanh sau để giảm tốc độ.', 'Nhả hết tay ga, tắt động cơ, sử dụng phanh trước và phanh sau để giảm tốc độ.', 'Sử dụng phanh trước để giảm tốc độ kết hợp với tắt chìa khóa điện của xe.', '', 1, 1, '', 'Chạy xe tay ga xuống dốc thì giữ tay ga ở mức phù hợp để máy xe hổ trợ giảm tốc độ.', 'luat', 'A2', 17),
(1093, 'Chủ phương tiện cơ giới đường bộ có được tự ý thay đổi màu sơn, nhãn hiệu hoặc các đặc tính kỹ thuật của phương tiện so với chứng nhận đăng ký xe hay không?', 'Được phép thay đổi bằng cách dán đề can với màu sắc phù hợp.', 'Không được phép thay đổi.', 'Tùy từng loại phương tiện cơ giới đường bộ.', '', 2, 0, '', 'Không tự ý thay đổi màu sơn xe, mọi việc thay đổi nhận dạng xe phải được cơ quan có thẩm quyền cho phép.', 'luat', 'A2', 17),
