(1106, 'Người lái xe điều khiển xe rẽ trái như thế nào là đúng quy tắc giao thông?', 'Rẽ trái ngay trước xe buýt. ', 'Rẽ trái trước xe tải.', 'Nhường đường cho xe buýt và xe tải.', '', 3, 0, '547.jpg', 'Xe tải đang trong giao lộ đi trước, tiếp đến xe buýt đi thẳng, cuối cùng xe của bạn rẽ trái.', 'sahinh', 'A2', 17),
(1107, 'Xe của bạn được đi theo hướng nào trong trường hợp này?', 'Đi thẳng hoặc rẽ trái. ', 'Đi thẳng hoặc rẽ phải. ', 'Rẽ trái.', '', 1, 0, '570.jpg', 'Chỉ được đi thẳng hoặc rẽ trái. Mũi tên dưới đường đã hướng dẫn.', 'sahinh', 'A2', 17),
(1108, 'Xe của bạn đang di chuyển gần đến khu vực giao cắt với đường sắt, khi rào chắn đang dịch chuyển, bạn điều khiển xe như thế nào là đứng quy tắc giao thông?', 'Quan sát nếu thấy không có tầu thì tăng tốc cho xe vượt qua đường sắt.', 'Dừng lại trước rào chắn một khoảng cách an toàn.', 'Ra tín hiệu, yêu cầu người gác chắn tàu kéo chậm Barie để xe bạn qua.', '', 2, 0, '596.jpg', 'Người ta kéo rào chắn là báo sắp có tàu hỏa chạy qua thì mình phải dừng lại.', 'sahinh', 'A2', 17),
(1109, 'Hành vi đưa xe cơ giới, xe máy chuyên dùng không bảo đảm tiêu chuẩn an toàn kỹ thuật và bảo vệ môi trường vào tham gia giao thông đường bộ có bị nghiêm cấm hay không?', 'Không nghiêm cấm. ', 'Bị nghiêm cấm.', 'Bị nghiêm cấm tuỳ theo các tuyến đường. ', 'Bị nghiêm cấm tuỳ theo loại xe.', 2, 1, '', 'Gặp đáp án có BỊ NGHIÊM CẤM thì chọn ngay là chắc chắn đúng!', 'luat', 'A2', 18),
(1110, 'Người lái xe không được vượt xe khác khi gặp trường hợp nào ghi ở dưới đây?', 'Trên cầu hẹp có một làn xe. Nơi đường giao nhau, đường bộ giao nhau cùng mức với đường sắt; xe được quyền ưu tiên đang phát tín hiệu ưu tiên đi làm nhiệm vụ.', 'Trên cầu có từ 02 làn xe trở lên; nơi đường bộ giao nhau không cùng mức với đường sắt; xe được quyền ưu tiên đang đi phía trước nhưng không phát tín hiệu ưu tiên.', 'Trên đường có 2 làn đường được phân chia làn bằng vạch kẻ nét đứt.', '', 1, 1, '', 'Trên cầu có 1 làn xe KHÔNG ĐƯỢC VƯỢT.', 'luat', 'A2', 18),
(1111, 'Người đủ 16 tuổi được điều khiển các loại xe nào dưới đây?', 'Xe mô tô 2 bánh có dung tích xi-lanh từ 50 cm3 trở lên. ', 'Xe gắn máy có dung tích xi-lanh dưới 50 cm3.', 'Xe ô tô tải dưới 3,5 tấn; xe chở người đến 9 chỗ ngồi. ', 'Tất cả các ý nêu trên.', 2, 0, '', '16 tuổi được chạy xe dưới 50cm3.', 'luat', 'A2', 18),
(1112, 'Bạn đang lái xe trong khu vực đô thị từ 22 giờ đến 5 giờ sáng hôm sau và cần vượt một xe khác, bạn cần báo hiệu như thế nào để đảm bảo an toàn giao thông?', 'Phải báo hiệu bằng đèn hoặc còi;', 'Chỉ được báo hiệu bằng còi.', 'Xe Phải báo hiệu bằng cả còi và đèn. ', 'Chỉ được báo hiệu bằng đèn.', 4, 0, '', 'Từ 22 giờ tối tới 5 giờ sáng trong khu đô thị mà muốn vượt thì chỉ nháy đèn báo hiệu.', 'luat', 'A2', 18),
(1113, 'Người điều khiển xe mô tô hai bánh, xe gắn máy không được thực hiện những hành vi nào dưới đây?', 'Đi vào phần đường dành cho người đi bộ và phương tiện khác; sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính), đi xe dàn hàng ngang.', 'Chở 02 người; trong đó, có người bệnh đi cấp cứu hoặc trẻ em dưới 14 tuổi hoặc áp giải người có hành vi vi phạm pháp luật.', 'Điều khiển phương tiện tham gia giao thông trên đường tỉnh lộ hoặc quốc lộ.', '', 1, 0, '', 'Không được chạy xe trong đường dành cho người đi bộ.', 'luat', 'A2', 18),
(1114, 'Trên đường bộ ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 70 km/h?', 'Ô tô buýt; ô tô đầu kéo kéo sơ mi rơ moóc; xe mô tô; ô tô chuyên dùng (trừ ô tô trộn vữa, ô tô trộn bê tông).', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3,5 tấn.', 1, 0, '', 'Đường đôi có giải phân cách ngoài khu vực đông dân cư tốc độ xe chạy: 60 km/h: ÔTÔ XI TÉC, ô tô kéo rơ moóc. 70 km/h: Xe MÔTÔ, ÔTÔ Buýt. 80 km/h: Xe ÔTÔ trên 30 chỗ, ôtô TẢI trên 3,5 tấn. 90 km/h: Xe ÔTÔ CON, Xe ÔTÔ dưới 30 chỗ, ÔTÔ Tải.', 'luat', 'A2', 18),
(1115, 'Tại nơi đường giao nhau, người lái xe đang đi trên đường không ưu tiên phải xử lý như thế nào là đúng quy tắc giao thông?', 'Tăng tốc độ qua đường giao nhau để đi trước xe đi trên đường ưu tiên.', 'Giảm tốc độ qua đường giao nhau để đi trước xe đi trên đường ưu tiên.', 'Nhường đường cho xe đi trên đường ưu tiên từ bất kỳ hướng nào tới.', '', 3, 0, '', 'Đang đi trên đường không ưu tiên thì phải nhường đường cho xe trên đường ưu tiên.', 'luat', 'A2', 18),
(1116, 'Khái niệm về văn hóa giao thông được hiểu như thế nào là đúng?', 'Là sự hiểu biết và chấp hành nghiêm chỉnh pháp luật về giao thông; là ý thức trách nhiệm với cộng đồng khi tham gia giao thông.', 'Là ứng xử có văn hóa, có tình yêu thương con người trong các tình huống không may xảy ra khi tham gia giao thông.', 'Cả ý 1 và ý 2.', '', 3, 0, '', 'Đáp án có Cả ý 1 và ý 2 thì phải đọc kỹ và phân tích. Thấy hợp lý cả 2 thì chọn đáp án 3.', 'luat', 'A2', 18),
(1117, 'Khi quay đầu xe, người lái xe cần phải quan sát và thực hiện các thao tác nào để đảm bảo an toàn giao thông?', 'Quan sát biển báo hiệu để biết nơi được phép quay đầu; quan sát kỹ địa hình nơi chọn để quay đầu; lựa chọn quỹ đạo quay đầu xe cho thích hợp; quay đầu xe với tốc độ thấp; thường xuyên báo tín hiệu để người, các phương tiện xung quanh được biết; nếu quay đầu xe ở nơi nguy hiểm thì đưa đầu xe về phía nguy hiểm đưa đuôi xe về phía an toàn.', 'Quan sát biển báo hiệu để biết nơi được phép quay đầu; quan sát kỹ địa hình nơi chọn để quay đầu; lựa chọn quỹ đạo quay đầu xe; quay đầu xe với tốc độ tối đa; thường xuyên báo tín hiệu để người, các phương tiện xung quanh được biết; nếu quay đầu xe ở nơi nguy hiểm thì đưa đuôi xe về phía nguy hiểm và đầu xe về phía an toàn.', '', '', 1, 0, '', 'Quay đầu xe với tốc độ thấp.Quay đầu xe về phía nguy hiểm.', 'luat', 'A2', 18),
(1118, 'Xe mô tô và xe ô tô tham gia giao thông trên đường bộ phải bắt buộc có đủ bộ phận giảm thanh không?', 'Không bắt buộc. ', 'Bắt buộc.', 'Tùy từng trường hợp.', '', 2, 0, '', 'Gặp đán án có chữ bắt buộc thì chọn ngay là chắc chắn đúng', 'luat', 'A2', 18),
