(1235, 'Theo tín hiệu đèn của xe cơ giới, xe nào vi phạm quy tắc giao thông?', 'Xe mô tô.', 'Xe ô tô con.', 'Không xe nào vi phạm. ', 'Cả hai xe.', 4, 0, '559.jpg', '', 'sahinh', 'B1', 1),
(1236, 'Bạn xử lý như thế nào khi lái xe ô tô vượt qua đoàn người đi xe đạp có tổ chức?', 'Tăng tốc độ, chuyển sang làn đường bên trái để vượt.', 'Không được vượt những người đi xe đạp.', '', '', 2, 0, '577.jpg', '', 'sahinh', 'B1', 1),
(1237, 'Khi gặp xe ngược chiều bật đèn pha trong tình huống dưới đây, bạn xử lý như thế nào?', 'Bật đèn chiếu xa, tăng tốc độ vượt xe cùng chiều.', 'Giữ nguyên đèn chiếu gần, giảm tốc độ, đi sau xe phía trước.', 'Giữ nguyên đèn chiếu gần, tăng tốc độ vượt xe cùng chiều.', '', 2, 0, '595.jpg', '', 'sahinh', 'B1', 1),
(1238, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe đạp.', 'Xe mô tô, xe đạp, xe của bạn. ', 'Xe đạp, xe mô tô, xe của bạn.', '', 3, 0, '583.jpg', '', 'sahinh', 'B1', 1),
(1239, '“Làn đường” là gì?', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, sử dụng cho xe chạy.', 'Là một phần của phần đường xe chạy được chia theo chiều dọc của đường, có bề rộng đủ cho xe chạy an toàn.', 'Là đường cho xe ô tô chạy, dừng, đỗ an toàn.', '', 2, 0, '', '', 'luat', 'B1', 2),
(1240, 'Người điều khiển phương tiện giao thông đường bộ mà trong cơ thể có chất ma tuý có bị nghiêm cấm hay không?', 'Bị nghiêm cấm.', 'Không bị nghiêm cấm.', 'Không bị nghiêm cấm, nếu có chất ma tuý ở mức nhẹ, có thể điều khiển phương tiện tham gia giao thông.', '', 1, 1, '', '', 'luat', 'B1', 2),
(1241, 'Bạn đang lái xe phía trước có một xe cảnh sát giao thông không phát tín hiệu ưu tiên bạn có được phép vượt hay không?', 'Không được vượt.', 'Được vượt khi đang đi trên cầu.', 'Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', 'Được vượt khi đảm bảo an toàn.', 4, 0, '', '', 'luat', 'B1', 2),
(1242, 'Người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E), lái xe hạng D kéo rơ moóc (FD) phải đủ bao nhiêu tuổi trở lên?', '23 tuổi.', '24 tuổi.', '27 tuổi.', '30 tuổi', 3, 0, '', '', 'luat', 'B1', 2),
(1243, 'Biển báo hiệu hình tròn có nền xanh lam có hình vẽ màu trắng là loại biển gì dưới đây?', 'Biển báo nguy hiểm.', 'Biển báo cấm.', 'Biển báo hiệu lệnh phải thi hành. ', 'Biển báo chỉ dẫn.', 3, 0, '74.jpg', '', 'luat', 'B1', 2),
(1244, 'Khi muốn chuyển hướng, người lái xe phải thực hiện như thế nào để đảm bảo an toàn giao thông?', 'Quan sát gương, ra tín hiệu, quan sát an toàn và chuyển hướng.', 'Quan sát gương, giảm tốc độ, ra tín hiệu chuyển hướng, quan sát an toàn và chuyển hướng.', 'Quan sát gương, tăng tốc độ, ra tín hiệu và chuyển hướng.', '', 2, 0, '', '', 'luat', 'B1', 2),
(1245, 'Người điểu khiển xe mô tô hai bánh, xe gắn máy được phép chở tối đa 2 người trong những trường hợp nào?', 'Chở người bệnh đi cấp cứu; trẻ em dưới 14 tuổi. ', 'Áp giải người có hành vi vi phạm pháp luật.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 2),
(1246, 'Trên đường bộ (trừ đường cao tốc) trong khu vực đông dân cư, đường đôi hoặc đường một chiều có từ hai làn xe cơ giới trở lên, loại xe nào dưới đây được tham gia giao thông với tốc độ tối đa cho phép là 60 km/h?', 'Ô tô con, ô tô tải, ô tô chở người trên 30 chỗ.', 'Xe gắn máy, xe máy chuyên dùng.', 'Cả ý 1 và ý 2.', '', 1, 0, '', '', 'luat', 'B1', 2),
(1247, 'Tác dụng của mũ bảo hiểm đối với người ngồi trên xe mô tô hai bánh trong trường hợp xảy ra tai nạn giao thông là gì?', 'Để làm đẹp.', 'Để tránh mưa nắng.', 'Để giảm thiểu chấn thương vùng đầu.', 'Để các loại phương tiện khác dễ quan sát.', 3, 0, '', '', 'luat', 'B1', 2),
(1248, 'Trên đường bộ, người lái xe ô tô có được phép dừng xe, đỗ xe song song với một xe khác đang dừng, đỗ hay không?', 'Được phép.', 'Không được phép.', 'Chỉ được phép dừng, đỗ khi đường vắng.', '', 2, 0, '', '', 'luat', 'B1', 2),
(1249, 'Người nhận hàng có các nghĩa vụ gì dưới đây?', 'Nhận hàng hóa đúng thời gian, địa điểm đã thỏa thuận.', 'Xuất trình giấy vận chuyển và giấy tờ tùy thân cho người kinh doanh vận tải trước khi nhận hàng hóa; thanh toán chi phí phát sinh do nhận hàng chậm.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 2),
(1250, 'Khi sơ cứu người bị tai nạn giao thông đường bộ, có vết thương chảy máu ngoài, màu đỏ tươi phun thành tia và phun mạnh khi mạch đập, bạn phải làm gì dưới đây?', 'Thực hiện cầm máu trực tiếp.', 'Thực hiện cầm máu không trực tiếp (chặn động mạch).', '', '', 2, 0, '', '', 'luat', 'B1', 2),
(1251, 'Khi quay đầu xe, người lái xe cần phải quan sát và thực hiện các thao tác nào để đảm bảo an toàn giao thông?', 'Quan sát biển báo hiệu để biết nơi được phép quay đầu; quan sát kỹ địa hình nơi chọn để quay đầu; lựa chọn quỹ đạo quay đầu xe cho thích hợp; quay đầu xe với tốc độ thấp; thường xuyên báo tín hiệu để người, các phương tiện xung quanh được biết; nếu quay đầu xe ở nơi nguy hiểm thì đưa đầu xe về phía nguy hiểm đưa đuôi xe về phía an toàn.', 'Quan sát biển báo hiệu để biết nơi được phép quay đầu; quan sát kỹ địa hình nơi chọn để quay đầu; lựa chọn quỹ đạo quay đầu xe; quay đầu xe với tốc độ tối đa; thường xuyên báo tín hiệu để người, các phương tiện xung quanh được biết; nếu quay đầu xe ở nơi nguy hiểm thì đưa đuôi xe về phía nguy hiểm và đầu xe về phía an toàn.', '', '', 1, 0, '', '', 'luat', 'B1', 2),
(1252, 'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?', 'Phanh tay đang hãm. ', 'Thiếu dầu phanh.', 'Nhiệt độ nước làm mát tăng quá mức cho phép.', 'Dầu bôi trơn bị thiếu.', 4, 0, '236.jpg', '', 'luat', 'B1', 2),
(1253, 'Khi lùi xe, người lái xe phải xử lý như thế nào để bảo đảm an toàn giao thông?', 'Quan sát bên trái, bên phải, phía sau xe, có tín hiệu cần thiết và lùi xe với tốc độ phù hợp.', 'Quan sát phía trước xe và lùi xe với tốc độ nhanh.', 'Quan sát bên trái và phía trước của xe và lùi xe với tốc độ\nnhanh.', '', 1, 0, '', '', 'luat', 'B1', 2),
(1254, 'Xe mô tô và xe ô tô tham gia giao thông trên đường bộ phải bắt buộc có đủ bộ phận giảm thanh không?', 'Không bắt buộc. ', 'Bắt buộc.', 'Tùy từng trường hợp.', '', 2, 0, '', '', 'luat', 'B1', 2),
(1255, 'Hãy nêu công dụng hệ thống phanh của xe ô tô?', 'Dùng để giảm tốc độ, dừng chuyển động của xe ô tô và giữ cho xe ô tô đứng yên trên dốc', 'Dùng để thay đổi hướng chuyển động hoặc giữ cho xe ô tô chuyển động ổn định theo hướng xác định.', 'Dùng để truyền hoặc ngắt truyền động từ động cơ đến bánh xe chủ động của xe ô tô', '', 1, 0, '', '', 'luat', 'B1', 2),
