(1274, 'Khái niệm “khổ giới hạn của đường bộ” được hiểu như thế nào là đúng?', 'Là khoảng trống có kích thước giới hạn về chiều cao, chiều rộng của đường, cầu, bến phà, hầm đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.', 'Là khoảng trống có kích thước giới hạn về chiều rộng của đường, cầu, bến phà, hầm trên đường bộ để các xe kể cả hàng hóa xếp trên xe đi qua được an toàn.', 'Là khoảng trống có kích thước giới hạn về chiều cao của cầu, bến phà, hầm trên đường bộ để các xe đi qua được an toàn.', '', 1, 0, '', '', 'luat', 'B1', 3),
(1275, 'Việc lái xe mô tô, ô tô, máy kéo ngay sau khi uống rượu, bia có được phép hay không?', 'Không được phép.', 'Chỉ được lái ở tốc độ chậm và quãng đường ngắn. ', 'Chỉ được lái nếu trong cơ thể có nồng độ cồn thấp.', '', 1, 1, '', '', 'luat', 'B1', 3),
(1276, 'Bạn đang lái xe phía trước có một xe cứu thương đang phát tín hiệu ưu tiên bạn có được phép vượt hay không?', 'Không được vượt.', 'Được vượt khi đang đi trên cầu.', 'Được phép vượt khi đi qua nơi giao nhau có ít phương tiện cùng tham gia giao thông.', 'Được vượt khi đảm bảo an toàn.', 1, 0, '', '', 'luat', 'B1', 3),
(1277, 'Tuổi tối đa của người lái xe ô tô chở người trên 30 chỗ ngồi (hạng E) là bao nhiêu tuổi?', '55 tuổi đối với nam và 50 tuổi đối với nữ.', '55 tuổi đối với nam và nữ.', '60 tuổi đối với nam và 55 tuổi đối với nữ. ', '45 tuổi với nam và 40 tuổi với nữ.', 1, 0, '', '', 'luat', 'B1', 3),
(1278, 'Biển báo hiệu hình chữ nhật hoặc hình vuông hoặc hình mũi tên nền xanh lam là loại biển gì dưới đây?', 'Biển báo nguy hiểm.', 'Biển báo cấm.', 'Biển báo hiệu lệnh phải thi hành. ', 'Biển báo chỉ dẫn.', 4, 0, '75.jpg', '', 'luat', 'B1', 3),
(1279, 'Khi lùi xe người lái xe phải làm gì để bảo đảm an toàn?', 'Quan sát phía trước và cho lùi xe ở tốc độ chậm.', 'Lợi dụng nơi đường giao nhau đủ chiều rộng để lùi.', 'Phải quan sát phía sau, có tín hiệu cần thiết và chỉ khi nào thấy không nguy hiểm mới được lùi.', '', 3, 0, '', '', 'luat', 'B1', 3),
(1280, 'Người điều khiển xe mô tô hai bánh, xe gắn máy không được thực hiện những hành vi nào dưới đây?', 'Đi vào phần đường dành cho người đi bộ và phương tiện khác; sử dụng ô, điện thoại di động, thiết bị âm thanh (trừ thiết bị trợ thính), đi xe dàn hàng ngang.', 'Chở 02 người; trong đó, có người bệnh đi cấp cứu hoặc trẻ em dưới 14 tuổi hoặc áp giải người có hành vi vi phạm pháp luật.', 'Điều khiển phương tiện tham gia giao thông trên đường tỉnh lộ hoặc quốc lộ.', '', 1, 0, '', '', 'luat', 'B1', 3),
(1281, 'Trên đường bộ (trừ đường cao tốc) ngoài khu vực đông dân cư, đường đôi có dải phân cách giữa, loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 90 km/h?', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải đến 3,5 tấn.', 'Ô tô buýt, ô tô đầu kéo kéo sơ mi rơ moóc, ô tô chuyên dùng, xe mô tô.', '', 2, 0, '', '', 'luat', 'B1', 3),
