(1407, 'Xe tải kéo mô tô ba bánh như hình này có đúng quy tắc giao thông không?', 'Đúng.', 'Không đúng.', '', '', 2, 0, '510.jpg', '', 'sahinh', 'B1', 6),
(1408, 'Các xe đi như thế nào là đúng quy tắc giao thông?', 'Các xe ở phía tay phải và tay trái của người điều khiển được phép đi thẳng.', 'Cho phép các xe ở mọi hướng được rẽ phải.', 'Tất cả các xe phải dừng lại trước ngã tư, trừ những xe đã ở trong ngã tư được phép tiếp tục đi.', '', 3, 0, '528.jpg', '', 'sahinh', 'B1', 6),
(1409, 'Xe nào phải nhường đường là đúng quy tắc giao thông?', 'Xe con (A).', 'Xe con (B).', '', '', 2, 0, '546.jpg', '', 'sahinh', 'B1', 6),
(1410, 'Trong tình huống dưới đây, để tránh xe phía trước bị hỏng đột xuất trên đường, người lái xe phải làm gì?', 'Đánh lái sang trái cho xe vượt qua.', 'Quan sát phía trước, phía sau, khi đủ điều kiện an toàn, bật tín hiệu bằng đèn hoặc còi rồi cho xe chạy vượt qua.', 'Cấm vượt.', '', 2, 0, '564.jpg', '', 'sahinh', 'B1', 6),
(1411, 'Bạn xử lý như thế nào trong trường hợp này?', 'Tăng tốc độ và đi thẳng qua ngã tư. ', 'Dừng xe trước vạch dừng.', 'Giảm tốc độ và đi thẳng qua ngã tư.', '', 3, 0, '582.jpg', '', 'sahinh', 'B1', 6),
(1412, 'Trong tình huống dưới đây, xe đầu kéo kéo rơ moóc (xe container) đang rẽ phải, xe con màu xanh và xe máy phía sau xe container đi như thế nào để đảm bảo an toàn?', 'Vượt về phía bên phải để đi tiếp.', 'Giảm tốc độ chờ xe container rẽ xong rồi tiếp tục đi.', 'Vượt về phía bên trái để đi tiếp.', '', 2, 0, '600.jpg', '', 'sahinh', 'B1', 6),
(1413, 'Xe nào được đi trước trong trường hợp này?', 'Xe con.', 'Xe của bạn.', '', '', 2, 0, '588.jpg', '', 'sahinh', 'B1', 6),
(1414, 'Đường mà trên đó phương tiện tham gia giao thông được các phương tiện giao thông đến từ hướng khác nhường đường khi qua nơi đường giao nhau, được cắm biển báo hiệu đường ưu tiên là loại đường gì?', 'Đường không ưu tiên. ', 'Đường tỉnh lộ.', 'Đường quốc lộ. ', 'Đường ưu tiên.', 4, 0, '', '', 'luat', 'B1', 7),
(1415, 'Hành vi giao xe cơ giới, xe máy chuyên dùng cho người không đủ điều kiện để điều khiển xe tham gia giao thông có được phép hay không?', 'Chỉ được thực hiện nếu đã hướng dẫn đầy đủ.  ', 'Không được phép.', 'Được phép tuỳ từng trường hợp.', 'Chỉ được phép thực hiện với thành viên trong gia đình.', 2, 1, '', '', 'luat', 'B1', 7),
(1416, 'Người điều khiển phương tiện giao thông trên đường phố có được dừng xe, đỗ xe trên miệng cống thoát nước, miệng hầm của đường điện thoại, điện cao thế, chỗ dành riêng cho xe chữa cháy lấy nước hay không?', 'Được dừng xe, đỗ xe trong trường hợp cần thiết.', 'Không được dừng xe, đỗ xe.', 'Được dừng xe, không được đỗ xe.', '', 2, 1, '', '', 'luat', 'B1', 7),
(1417, 'Người có GPLX mô tô hạng A1 được phép điều khiển loại xe nào dưới đây?', 'Xe mô tô hai bánh có dung tích xi-lanh từ 50 cm3 đến dưới 175 cm3.', 'Xe mô tô ba bánh dùng cho người khuyết tật. ', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1418, 'Việc sát hạch cấp giấy phép lái xe ô tô phải thực hiện ở đâu và như thế nào?', 'Tại các cơ sở đào tạo lái xe có đủ điều kiện và phải bảo đảm công khai minh bạch.', 'Tại sân tập lái của cơ sở đào tạo lái xe và phải đảm bảo công khai, minh bạch.', 'Tại các trung tâm sát hạch lái xe có đủ điều kiện hoạt động và phải bảo đảm công khai minh bạch.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1419, 'Tại nơi đường giao nhau, người lái xe đang đi trên đường không ưu tiên phải nhường đường như thế nào là đúng quy tắc giao thông?', 'Nhường đường cho xe đi ở bên phải mình tới. ', 'Nhường đường cho xe đi ở bên trái mình tới.', 'Nhường đường cho xe đi trên đường ưu tiên hoặc đường chính từ bất kỳ hướng nào tới.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1420, 'Tại ngã ba hoặc ngã tư không có đảo an toàn, người lái xe phải nhường đường như thế nào là đúng trong các trường hợp dưới đây?', 'Nhường đường cho người đi bộ đang đi trên phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ưu tiên, đường chính từ bất kỳ hướng nào tới; nhường đường cho xe ưu tiên, xe đi từ bên phải đến.', 'Nhường đường cho người đi bộ đang đứng chờ đi qua phần đường dành cho người đi bộ sang đường; nhường đường cho xe đi trên đường ngược chiều, đường nhánh từ bất kỳ hướng nào tới; nhường đường cho xe đi từ bên trái đến.', 'Không phải nhường đường.', '', 1, 0, '', '', 'luat', 'B1', 7),
(1421, 'Trên đường bộ ngoài khu vực đông dân cư, đường hai chiều không có dải phân cách giữa; đường một chiều có một làn xe cơ giới (trừ đường cao tốc), loại xe nào dưới đây tham gia giao thông với tốc độ tối đa cho phép là 80 km/h?', 'Ô tô kéo rơ moóc, ô tô kéo xe khác, xe gắn máy.', 'Ô tô chở người trên 30 chỗ (trừ ô tô buýt), ô tô tải có trọng tải trên 3,5 tấn.', 'Xe ô tô con, xe ô tô chở người đến 30 chỗ (trừ xe buýt), ô tô tải có trọng tải nhỏ hơn hoặc bằng 3,5 tấn.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1422, 'Khi điều khiển xe trên đường vòng, khuất tầm nhìn người lái xe cần phải làm gì để đảm bảo an toàn?', 'Đi đúng làn đường, đúng tốc độ quy định, không được vượt xe khác.', 'Đi sang làn đường của xe ngược chiều để mở rộng tầm nhìn và vượt xe khác.', 'Cho xe đi sát bên phải làn đường, bật tín hiệu báo hiệu để vượt bên phải xe khác.', '', 1, 0, '', '', 'luat', 'B1', 7),
(1423, 'Việc vận chuyển hàng hóa bằng xe ô tô phải chấp hành các quy định nào dưới đây?', 'Hàng vận chuyển trên xe phải được xếp đặt gọn gàng và chằng buộc chắc chắn.', 'Khi vận chuyển hàng rời phải che đậy, không để rơi vãi. ', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1424, 'Người có văn hóa giao thông khi điều khiển xe cơ giới tham gia giao thông đường bộ phải đảm bảo các điều kiện gì dưới đây?', 'Có giấy phép lái xe phù hợp với loại xe được phép điều khiển; xe cơ giới đảm bảo các quy định về chất lượng, an toàn kỹ thuật và bảo vệ môi trường.', 'Có giấy chứng nhận bảo hiểm trách nhiệm dân sự của chủ xe cơ giới còn hiệu lực; nộp phí sử dụng đường bộ theo quy định.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1425, 'Khi điều khiển xe trên đường vòng người lái xe cần phải làm gì để đảm bảo an toàn?', 'Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; giảm tốc độ tới mức cần thiết, về số thấp và thực hiện quay vòng với tốc độ phù hợp với bán kính cong của đường vòng.', 'Quan sát cẩn thận các chướng ngại vật và báo hiệu bằng còi, đèn; tăng tốc để nhanh chóng qua đường vòng, đạp ly hợp (côn) và giảm tốc độ sau khi qua đường vòng.', '', '', 1, 0, '', '', 'luat', 'B1', 7),
(1426, 'Các biện pháp tiết kiệm nhiên liệu khi chạy xe?', 'Bảo dưỡng xe theo định kỳ và có kế hoạch lộ trình trước khi chạy xe.', 'Kiểm tra áp suất lốp theo quy định và chạy xe với tốc độ phù hợp với tình trạng mặt đường và mật độ giao thông trên đường.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 7),
(1427, 'Trong trường hợp bất khả kháng, khi dừng xe, đỗ xe trên làn dừng khẩn cấp của đường cao tốc người lái xe phải xử lý như thế nào dưới đây là đúng quy tắc giao thông?', 'Bật đèn cảnh báo sự cố, di chuyển phương tiện đến vị trí sát lền đường', 'Sử dụng các thiết bị cảnh báo như chóp nón, biển báo, đèn chớp... đặt phía sau xe để cảnh báo các phương tiện.', 'Gọi số điện thoại khẩn cấp của đường cao tốc để được hỗ trợ nếu xe gặp sự cố, tai nạn, hoặc các trường hợp khẩn cấp không thể di chuyển bình thường.', 'Tất cả các ý nêu trên.', 4, 0, '', '', 'luat', 'B1', 7),
(1428, 'Mục đích của bảo dưỡng thường xuyên đối với xe ô tô có tác dụng gì dưới đây?', 'Bảo dưỡng ô tô thường xuyên làm cho ô tô luôn luôn có tính năng kỹ thuật tốt, giảm cường độ hao mòn của các chi tiết, kéo dài tuổi thọ của xe.', 'Ngăn ngừa và phát hiện kịp thời các hư hỏng và sai lệch kỹ thuật để khắc phục, giữ gìn được hình thức bên ngoài. ', 'Cả ý 1 và ý 2. ', '', 3, 0, '', '', 'luat', 'B1', 7),
(1429, 'Âm lượng của còi điện lắp trên ô tô (đo ở độ cao 1,2 mét với khoảng cách 2 mét tính từ đầu xe) là bao nhiêu?', 'Không nhỏ hơn 90 dB (A), không lớn hơn 115 dB (A). ', 'Không nhỏ hơn 80 dB (A), không lớn hơn 105 dB (A).', 'Không nhỏ hơn 70 dB (A), không lớn hơn 90 dB (A).', '', 1, 0, '', '', 'luat', 'B1', 7),
(1430, 'Khi động cơ ô tô đã khởi động, bảng đồng hồ xuất hiện ký hiệu như hình vẽ dưới đây, báo hiệu tình trạng như thế nào của xe ô tô?', 'Báo hiệu hệ thống chống bó cứng khi phanh bị lỗi.', 'Áp suất lốp không đủ.', 'Đang hãm phanh tay. ', 'Sắp hết nhiên liệu.', 1, 0, '295.jpg', '', 'luat', 'B1', 7);
