(1445, 'Các xe đi theo hướng mũi tên, xe nào chấp hành đúng quy tắc giao thông?', 'Xe tải, mô tô.', 'Xe khách, mô tô. ', 'Xe tải, xe con.', 'Mô tô, xe con.', 2, 0, '565.jpg', '', 'sahinh', 'B1', 7),
(1446, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe đạp.', 'Xe mô tô, xe đạp, xe của bạn. ', 'Xe đạp, xe mô tô, xe của bạn.', '', 3, 0, '583.jpg', '', 'sahinh', 'B1', 7),
(1447, 'Xe của bạn được đi theo hướng nào trong trường hợp này?', 'Chuyển sang làn đường bên phải và rẽ phải.', 'Dừng lại trước vạch dừng và rẽ phải khi đèn xanh.', 'Dừng lại trước vạch dừng và đi thẳng hoặc rẽ trái khi đèn xanh. ', '', 3, 0, '571.jpg', '', 'sahinh', 'B1', 7),
(1448, 'Xe nào được đi trước trong trường hợp này?', 'Xe con, xe tải, xe của bạn.', 'Xe tải, xe con, xe của bạn. ', 'Xe tải, xe của bạn, xe con.', '', 2, 0, '589.jpg', '', 'sahinh', 'B1', 7),
(1449, 'Khái niệm “phương tiện giao thông cơ giới đường bộ” được hiểu thế nào là đúng?', 'Gồm xe ô tô; máy kéo; xe mô tô hai bánh; xe mô tô ba bánh; xe gắn máy; xe cơ giới dùng cho người khuyết tật và xe máy chuyên dùng.', 'Gồm xe ô tô; máy kéo; rơ moóc hoặc sơ mi rơ moóc được kéo bởi xe ô tô, máy kéo; xe mô tô hai bánh; xe mô tô ba bánh, xe gắn máy (kể cả xe máy điện) và các loại xe tương tự.', '', '', 2, 0, '', '', 'luat', 'B1', 8),
(1450, 'Hành vi điều khiển xe cơ giới chạy quá tốc độ quy định, giành đường, vượt ẩu có bị nghiêm cấm hay không?', 'Bị nghiêm cấm tuỳ từng trường hợp. ', 'Không bị nghiêm cấm.', 'Bị nghiêm cấm.', '', 3, 1, '', '', 'luat', 'B1', 8),
