(1589, '“Người điều khiển phương tiện tham gia giao thông đường bộ” gồm những đối tượng nào dưới đây?', 'Người điều khiển xe cơ giới, người điều khiển xe thô sơ.', 'Người điều khiển xe máy chuyên dùng tham gia giao thông đường bộ.', 'Cả ý 1 và ý 2.', '', 3, 0, '', '', 'luat', 'B1', 12),
(1590, 'Hành vi vượt xe tại các vị trí có tầm nhìn hạn chế, đường vòng, đầu dốc có bị nghiêm cấm hay không?', 'Không bị nghiêm cấm.', 'Không bị nghiêm cấm khi rất vội. ', 'Bị nghiêm cấm.', 'Không bị nghiêm cấm khi khẩn cấp.', 3, 1, '', '', 'luat', 'B1', 12),
(1591, 'Người ngồi trên xe mô tô hai bánh, ba bánh, xe gắn máy khi tham gia giao thông có được mang, vác vật cồng kềnh hay không?', 'Được mang, vác tuỳ trường hợp cụ thể.', 'Không được mang, vác.', 'Được mang, vác nhưng phải đảm bảo an toàn.', 'Được mang, vác tùy theo sức khỏe của bản thân.', 2, 1, '', '', 'luat', 'B1', 12),
(1592, 'Người có giấy phép lái xe hạng B2 được điều khiển loại xe nào dưới đây?', 'Xe ô tô chở người trên 9 chỗ ngồi; xe ô tô tải, máy kéo có trọng tải trên 3,5 tấn.', 'Xe ô tô chở người đến 9 chỗ ngồi; xe ô tô tải, máy kéo có trọng tải dưới 3,5 tấn.', 'Xe ô tô chở người từ 10 đến 30 chỗ ngồi; xe ô tô tải, máy kéo có trọng tải trên 3,5 tấn.', '', 2, 0, '', '', 'luat', 'B1', 12),
(1593, 'Trên đường giao thông, khi hiệu lệnh của người điều khiển giao thông trái với hiệu lệnh của đèn hoặc biển báo hiệu thì người tham gia giao thông phải chấp hành theo hiệu lệnh nào?', 'Hiệu lệnh của người điều khiển giao thông. ', 'Hiệu lệnh của đèn điều khiển giao thông.', 'Hiệu lệnh của biển báo hiệu đường bộ.', 'Theo quyết định của người tham gia giao thông nhưng phải bảo đảm an toàn.', 1, 1, '', '', 'luat', 'B1', 12),
(1594, 'Trên đường cao tốc, người lái xe phải dừng xe, đỗ xe như thế nào để đảm bảo an toàn giao thông?', 'Không được dừng xe, đỗ xe hoặc chỉ được dừng xe, đỗ xe ở nơi đường rộng, nếu dừng, đỗ xe ở nơi đường hẹp phải sử dụng còi báo hiệu để người lái xe khác biết.', 'Chỉ được dừng xe, đỗ xe ở nơi quy định, trường hợp buộc phải dừng xe, đỗ xe không đúng nơi quy định thì người lái xe phải đưa xe ra khỏi phần đường xe chạy, nếu không thể được thì phải báo hiệu để người lái xe khác biết.', 'Chỉ được dừng xe, đỗ xe ở nơi đường rộng; trường hợp dừng xe, đỗ xe tại nơi đường hẹp phải đặt các chướng ngại vật trên đường để yêu cầu người lái xe khác giảm tốc độ để bảo đảm an toàn.', '', 2, 0, '', '', 'luat', 'B1', 12),
(1595, 'Người lái xe mô tô xử lý như thế nào khi cho xe mô tô phía sau vượt?', 'Nếu đủ điều kiện an toàn, người lái xe phải giảm tốc độ, đi sát về bên phải của phần đường xe chạy cho đến khi xe sau đã vượt qua, không được gây trở ngại đối với xe xin vượt.', 'Lái xe vào lề đường bên trái và giảm tốc độ để xe phía sau vượt qua, không được gây trở ngại đối với xe xin vượt.', 'Nếu đủ điều kiện an toàn, người lái xe phải tăng tốc độ, đi sát về bên phải của phần đường xe chạy cho đến khi xe sau đã vượt qua.', '', 1, 0, '', '', 'luat', 'B1', 12),
(1596, 'Khi tham gia giao thông trên đoạn đường không có biển báo “Cự ly tối thiểu giữa hai xe”, với điều kiện mặt đường khô ráo, xe cơ giới đang chạy với tốc độ từ trên 80 km/h đến 100 km/h, người lái xe phải giữ khoảng cách an toàn với xe đang chạy liền trước tối thiểu là bao nhiêu?', '35 m.', '55 m.', '70 m.', '', 3, 0, '', '', 'luat', 'B1', 12),
(1597, 'Khi tham gia giao thông trên đường cao tốc, người lái xe, người điều khiển xe máy chuyên dùng phải thực hiện như thế nào là đúng quy tắc giao thông?', 'Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe.', 'Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe chỉ vào ban ngày.', 'Tuân thủ tốc độ tối đa, tốc độ tối thiểu ghi trên biển báo hiệu đường bộ, sơn kẻ mặt đường trên các làn xe chỉ vào ban đêm.', '', 1, 0, '', '', 'luat', 'B1', 12),
(1598, 'Người kinh doanh vận tải hành khách có những quyền hạn nào dưới đây?', 'Tự ý thay đổi vị trí đón, trả khách theo hợp đồng vận chuyển hoặc đón thêm người ngoài danh sách hành khách đã ký.', 'Từ chối vận chuyển trước khi phương tiện rời bến, rời vị trí đón trả khách theo hợp đồng vận chuyển những người đã có vé hoặc người trong danh sách hợp đồng có hành vi gây rối trật tự công cộng, gây cản trở công việc kinh doanh vận tải, ảnh hưởng đến sức khỏe, tài sản của người khác, gian lận vé hoặc hành khách đang bị dịch bệnh nguy hiểm. ', 'Cả ý 1 và ý 2.', '', 2, 0, '', '', 'luat', 'B1', 12),
(1599, 'Hình nào dưới đây biểu hiện hàng chuyên chở là chất lỏng?', 'Hình 1.', 'Hình 2.', '', '', 2, 0, '192.jpg', '', 'luat', 'B1', 12),
(1600, 'Khi xảy ra tai nạn giao thông, những hành vi nào dưới đây bị nghiêm cấm?', 'Xâm phạm tính mạng, sức khoẻ, tài sản của người bị nạn và người gây tai nạn.', 'Bỏ trốn sau khi gây ra tai nạn để trốn tránh trách nhiệm. ', 'Cả ý 1 và ý 2.', '', 3, 1, '', '', 'luat', 'B1', 12),
(1601, 'Khi điều khiển xe ô tô tự đổ, người lái xe cần chú ý những điểm gì để đảm bảo an toàn?', 'Khi chạy trên đường xấu, nhiều ổ gà nên chạy chậm để thùng xe không bị lắc mạnh, không gây hiện tượng lệch “ben”; khi chạy vào đường vòng, cần giảm tốc độ, không lấy lái gấp và không phanh gấp.', 'Khi chạy trên đường quốc lộ, đường bằng phẳng không cần hạ hết thùng xe xuống.', 'Khi đổ hàng phải chọn vị trí có nền đường cứng và phẳng, dừng hẳn xe, kéo chặt phanh tay; sau đó mới điều khiển cơ cấu nâng “ben” để đổ hàng, đổ xong hàng mới hạ thùng xuống.', 'Cả ý 1 và ý 3.', 4, 0, '', '', 'luat', 'B1', 12);
